float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

vec2 Rot2D (vec2 q, float a)
{  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);}

vec2 Rot2Cs (vec2 q, vec2 cs)
{  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));}


vec3 DodecSym (vec3 p)
{
  float a, w;
  w = 2. * pi / 5.;

  float dihedDodec = 0.5 * atan (2.);
  vec2 csD = vec2 (cos (dihedDodec), - sin (dihedDodec));
  vec2 csD2 = vec2 (cos (2. * dihedDodec), - sin (2. * dihedDodec));

  p.xz = Rot2Cs (vec2 (p.x, abs (p.z)), vec2 (csD.x, - csD.y));
  p.xy = Rot2D (p.xy, - 0.25 * w);
  p.x = - abs (p.x);
  for (int k = 0; k < 3; k ++) {
    if (dot (p.yz, csD) > 0.) p.zy = Rot2Cs (p.zy, csD2) * vec2 (1., -1.);
    p.xy = Rot2D (p.xy, - w);
  }
  if (dot (p.yz, csD) > 0.) p.zy = Rot2Cs (p.zy, csD2) * vec2 (1., -1.);
  a = mod (atan (p.x, p.y) + 0.5 * w, w) - 0.5 * w;
  p.yx = vec2 (cos (a), sin (a)) * length (p.xy);
  p.xz = - vec2 (abs (p.x), p.z);
  return p;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

vec3 esferaPicos(vec3 p)
{	
   vec3 res= vec3(9999.0, -1.0,-1.0);

    float d=9999.9;
    vec3 q = DodecSym (p);
    //q.z += 0.48;
    q.z += 0.7;

     d = 0.9 * SmoothMin (d, PrCylDf (q, 0.05 * (0.55 + 1.5 * q.z), 0.3), 0.01);

    float sds1= sdSphere( p, 0.5 );

    res =opU3(res, vec3(d,2.0,-1.0));
    res =opU3(res, vec3(sds1,4.0,-1.0));
    return res;
}    


vec3 opScalePicos( vec3 p )
{
    float s=4.5; 
     vec3 res=esferaPicos(p/s);
    res.x*=s;
    return res;
}

