    vec3 rep2 = opAngRep(p, tau / numItem);
    vec3 rep3 = opAngRep(p, tau / (numItem/2.0));

    float diam=8.0;
	float sdsp1=sdSphere(p, diam);
    float sdsp2=sdSphere(p, diam+0.5);
    float sdb1= sdBox(rep2, vec3(8.5,0.2,diam));
