vec3 GetNormal(vec3 p)
{
	float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);

    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 999.0; 

    for(int i=0; i<MAX_STEPS; i++) 
    {
    	p = ro + rd*dO;
        dS = GetDist(p);
       if ( abs(dS.x)<MIN_DIST)
        { mObj.hitbln = true; minDist = abs(dO);break; }
       if(dO>MAX_DIST)
        { mObj.hitbln = false; minDist = min(minDist, dO);break; }
        dO += dS.x;
        marchCount++;
    }

    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return dO;
}
