// interpolated variant of https://shadertoy.com/view/sdsXzB

#define keyToggle(a) ( texelFetch(iChannel3,ivec2(a,2),0).x > 0.)
#define hash(p )     ( 2.* fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123) - 1. )
#define draw(v,d)      clamp(1. - abs( fract(v-.5) - .5 ) / (d), 0.,1.)
#define hue(v)       ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

#define func( P )    ( mod(iTime,4.) > 2. ? perlin( .5*(P) ) : noise( P ) )
//#define func( P )  ( keyToggle(32) ? perlin( .5*(P) ) : noise( P ) )
#define grad(x,y)      dot( hash( i+vec2(x,y) ), f-vec2(x,y) )

float noise( vec2 p )
{
    vec2 i = floor(p), f = fract(p), // u = f*f*(3.-2.*f);              // = smoothstep
                                        u = f*f*f*( 10. +f*(6.*f-15.)); // better with derivatives
    return mix( mix( grad(0,0), grad(1,0), u.x),
                mix( grad(0,1), grad(1,1), u.x), u.y);
}

float perlin( vec2 U ) { // inspired from https://www.shadertoy.com/view/XdXGW8
    float v = 0., s = .5;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    for( int i=0; i<3; i++, s/=2., U*=m )
        v  += s * noise( U ); 
    return v;
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, eps = vec2(1e-3,0),
         U = u / R.y,
         P = 8.*U - vec2(0.,.5*iTime);	
    O-=O;
	float l, dl, f = func(P), df;
    if ( int(u.y) == int(R.y*.4) ) { O.r++; return; }             // red separator
    if ( U.y > .4 ) {
#if 0
        l = exp2(floor(log2(2.*fwidth(P.x)/fwidth(f))));          // subvid amount (relative)
        dl =     fract(log2(2.*fwidth(P.x)/fwidth(f)));      
#else
        l = exp2(floor(log2(1./22./fwidth(f))));                  // subvid amount (absolute)
        dl =     fract(log2(1./22./fwidth(f)));
#endif

#if 1                                                             // draw isolines using sin
        O = vec4(.5+.5*  mix( sin(50.*l*f) , sin(100.*l*f), dl ) ); 
#else                                                             // draw isolines 1-pixel thick
        df = fwidth(f);
     // df = length( ( vec2( func(P + eps.xy), func(P + eps.yx) ) -f ) / eps.x )*fwidth(P.x);
        l *= 8.; 
        O += mix( draw(    f*l,    l*df ),
                  draw( 2.*f*l, 2.*l*df ),
                  dl );
#endif
    } 
    else {
        f *= 8.;
        O += draw( f, fwidth(f) );                                // draw isolines
    }

    O = sqrt(O);                                                  // to sRGB
}
