// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}


float columnas(vec3 p)
{
 p.y+=6.0;
    p.x= opRep1D(p.x, 15.0 );
    p.z= opRep1D(p.z, 10.0 );

    float  distToCapsule =sdCylinder( p, vec2(0.5,2.0) );
    //superior
    float sdb1a = sdBox( p-vec3(0.0,2.5,0.0), vec3(1.0,0.125,1.0));
    float sdb1b = sdBox( p-vec3(0.0,2.2,0.0), vec3(1.5,0.125,1.5));
    float sdb1c = sdBox( p-vec3(0.0,2.0,0.0), vec3(1.0,0.125,1.25));
    float sdb1d = sdBox( p-vec3(0.0,2.7,0.0), vec3(0.8,0.125,0.7));
    float sdb1e = sdBox( p-vec3(0.0,2.7,0.0), vec3(0.5,1.0,0.7));


    //inferior
    float sdb2a = sdBox( p-vec3(0.0,-2.0,0.0), vec3(1.0,0.125,1.0));
    float sdb2b = sdBox( p-vec3(0.0,-2.5,0.0), vec3(1.5,0.25,1.0));

    float  d=999.9;
    d= unionSDF(d,distToCapsule); 
    d= unionSDF(d,sdb1a);
    d= unionSDF(d,sdb1b);
    d= unionSDF(d,sdb1c);
    d= unionSDF(d,sdb1d);
    d= unionSDF(d,sdb1e);

    d= unionSDF(d,sdb2a);
    d= unionSDF(d,sdb2b);
    return d;
}   


