vec3 GetDist(vec3 p  ) 
{	
    vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 pp=p;

	float planeDist1 = p.y+0.0;  //piso inf
    res =opU3(res, vec3(planeDist1,101.0,-1.0)); //inf

    p.y=p.y-10.0;

    vec3 p2=p-vec3(.0,7.,.0);
    p2= rotate_y( p2, radians(iTime*20.0));


    float sdb1= sdBox(p2, vec3(10.0,2.0,2.0) );  //horizontal
    float sdb2= sdBox(p-vec3(.0,2.0,.0), vec3(2.0,13.0,2.0) );  //vertical

    float sds1= sdSphere( p-vec3(.0,7.,.0), 4.5 );
    float sds2= sdSphere( p-vec3(.0,7.,.0), 3.0 );

    float sdif1= differenceSDF(sdb1, sds1);
    float sdif2= differenceSDF(sdb2, sds1);

    res =opU3(res, vec3(sdif1,-1.0,11.0)); 
    res =opU3(res, vec3(sdif2,-1.0,10.0)); 
    res =opU3(res, vec3(sds2,3.0,-1.0)); 


    float c1=columnas(p);
    res =opU3(res, vec3(c1,1.0,-1.0)); 


    return res;
}

//------------------------------------------------
vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

