#define TEXT_ROWS 2

vec4 char(vec2 p, int c) {
    if (p.x < 0. || p.x > 1. || p.y < 0.|| p.y > 1.) return vec4(0,0,0,1);
    return texture(iChannel0, p/16. + fract(vec2(c, 15-c/16)/16.));
}

#define A(c) text = min(text, char(uv * float(TEXT_ROWS) + vec2(-0.5 * cursor.x, cursor.y) + 0.02 * (hash22(cursor) - 0.5), c).w); cursor.x++;
#define CRLF cursor.x = 0.; cursor.y++;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.y;
    float text = 1.;
    vec2 cursor = vec2(0, 1 - TEXT_ROWS);

    A(0x48) A(0x65) A(0x6c) A(0x6c) A(0x6f) A(0x2c) CRLF // Hello,\n
    A(0x57) A(0x6f) A(0x72) A(0x6c) A(0x64) A(0x21)      // World!

    text += 0.02 * (FBM(20. * uv.xyy) - 0.5);
    text += 0.01 * (FBM(100. * uv.xyy) - 0.5);
    float ink = smoothstep(0.5, 0.49, text);
    ink -= smoothstep(0.65, 0.75, FBM(25. * uv.xyy));
    fragColor = vec4(0.9, 0.85, 0.8, 1);
    fragColor = mix(fragColor, vec4(0.8, 0.75, 0.7, 1), smoothstep(0.55, 0.49, text));
    fragColor = mix(fragColor, vec4(0), clamp(ink, 0., 1.));
    fragColor += 0.05 * FBM(300. * uv.xyy);
    fragColor = pow(fragColor, vec4(1./2.2));
}

