// Evaluates the parametric equation of a quadratic bezier
// ---> a(1-t)^2 + 2b(1-t)t + ct^2
vec2 posBezier(in vec2 a, in vec2 b, in vec2 c, in float t) {
    float tInv = 1.0 - t;
    return a * tInv * tInv + b * 2.0 * t * tInv + c * t * t;
}

// Computes the derivative of a bezier curve for a given parameter value
vec2 derivBezier(in vec2 v1, in vec2 v2, in vec2 v3, in float t) {
     return 2.0 * (v1 * (t - 1.0) + (1.0 - 2.0 * t) * v2 + v3 * t);
}

// Computes a basis matrix for a point on a 2D bezier curve
// Up is facing out from the curve, like you're riding along it
mat2 basisBezier(in vec2 v1, in vec2 v2, in vec2 v3, in float t) {
    vec2 right = normalize(v1 * (t - 1.0) + (1.0 - 2.0 * t) * v2 + v3 * t);
    return mat2(right, -right.y, right.x);
}

// Computes the bezier curve's arclength from tmin to tmax
// From this stack exchange: https://math.stackexchange.com/questions/12186/arc-length-of-b%C3%A9zier-curves
float arclenBezier(in vec2 v1, in vec2 v2, in vec2 v3, in float tmin, in float tmax) {
    vec2 ba = v2 - v1, cb = v3 - v2;
    vec2 cbba = cb - ba;

    float a = dot(cbba, cbba);
    float b = dot(ba, cbba);
    float c = dot(ba, ba);

    vec4 pre = vec4(a * c - b * b, b / a, 2.0 * b, 0.0);
    pre.w = sqrt(pre.x);

    float atMin = a * tmin, atMax = a * tmax;
    float q1 = (tmax + pre.y) * sqrt(c + pre.z * tmax + atMax * tmax) - (tmin + pre.y) * sqrt(c + pre.z * tmin + atMin * tmin);
    float q2 = pre.x / pow(a, 1.5) * (asinh((atMax + b) / pre.w) - asinh((atMin + b) / pre.w));

    return q1 + q2;
}

// vec4(vertex, rotation, height)
vec4 bezierToParabola(in vec2 a, in vec2 b, in vec2 c) {
    vec2 v = a - 2.0 * b + c, ab = a - b; float d = v.y / v.x;
    float tVert = (d * ab.y + ab.x) / (v.x + d * v.y);

    float tVertInv = 1.0 - tVert;
    vec2 vert = a * tVertInv * tVertInv + b * 2.0 * tVertInv * tVert + c * tVert * tVert;

    vec2 right = normalize(a * (tVert - 1.0) + (1.0 - 2.0 * tVert) * b + c * tVert);
    mat2 basis = mat2(right, -right.y, right.x);

    vec2 aRel = a - vert;
    float aY = dot(aRel, basis[0]);
    return vec4(vert, atan(basis[0].y, basis[0].x), dot(aRel, basis[1]) / (aY * aY));
}

// vec2(shortest distance, parameter of closest point)
// clampRes flags whether the results should be held in the range [tmin, tmax]
float dot2(in vec2 v) { return dot(v, v); }
vec2 mapBezier(in vec2 p, in vec2 v1, in vec2 v2, in vec2 v3, in float tmin, in float tmax, in bool clampRes) {
    vec2 c1 = p - v1;
    vec2 c2 = 2.0 * v2 - v3 - v1;
    vec2 c3 = v1 - v2;

    // Cubic coefficients ---> t3*t^3 + t2*t^2 + t1*t + t0*t^0
    float t3 = dot(c2, c2);
    float t2 = dot(c3, c2) * 3.0;
    float t1 = dot(c1, c2) + 2.0 * dot(c3, c3);
    float t0 = dot(c1, c3);

    // Reduce by dividing by leading coefficient
    // This simplifies out a lot of things
    t2 /= t3, t1 /= t3, t0 /= t3;

    // Depressed cubic coefficients (p and q) and precomputation
    float t22 = t2 * t2;
    vec2 pq = vec2(t1 - t22 / 3.0, t22 * t2 / 13.5 - t2 * t1 / 3.0 + t0);
    float ppp = pq.x * pq.x * pq.x, qq = pq.y * pq.y;

    float p2 = abs(pq.x);
    float r1 = 1.5 / pq.x * pq.y;

    // Solutions and details gathered from here: https://en.wikipedia.org/wiki/Cubic_equation
    if (qq * 0.25 + ppp / 27.0 > 0.0) { // One real root, use hyperbolic trig
        float r2 = r1 * sqrt(3.0 / p2), root;
        if (pq.x < 0.0) root = sign(pq.y) * cosh(acosh(r2 * -sign(pq.y)) / 3.0);
        else root = sinh(asinh(r2) / 3.0);
        root = -2.0 * sqrt(p2 / 3.0) * root - t2 / 3.0;
        if (clampRes) root = clamp(root, tmin, tmax);
        return vec2(length(p - posBezier(v1, v2, v3, root)), root);
    }

    else { // Three real roots (only need to use two), use "normal" trig
        float ac = acos(r1 * sqrt(-3.0 / pq.x)) / 3.0; // 4pi/3 goes here --v
        vec2 roots = 2.0 * sqrt(-pq.x / 3.0) * cos(vec2(ac, ac - 4.18879020479)) - t2 / 3.0;
        if (clampRes) roots = clamp(roots, tmin, tmax);
        float d1 = dot2(p - posBezier(v1, v2, v3, roots.x));
        float d2 = dot2(p - posBezier(v1, v2, v3, roots.y));
        return d1 < d2 ? vec2(sqrt(d1), roots.x) : vec2(sqrt(d2), roots.t);
    }
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

// https://www.shadertoy.com/view/ws3GD7
float sdParabola(in vec2 pos, in float k) {
    pos.x = abs(pos.x);

    float ik = 1.0 / k;
    float p = ik * (pos.y - 0.5 * ik) / 3.0;
    float q = 0.25 * ik * ik * pos.x;

    float h = q * q - p * p * p;
    float r = sqrt(abs(h));

    float x = h > 0.0 ?
          // 1 root
          pow(q + r, 1.0 / 3.0) - pow(abs(q - r), 1.0 / 3.0) * sign(r - q) :
          // 3 roots
          2.0 * cos(atan(r, q) / 3.0) * sqrt(p);

    return length(pos - vec2(x, k * x * x)); // * sign(pos.x - x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;

    // Control points
    vec2 a = vec2(-0.5, 0.0);
    vec2 b = iMouse.z > 0.0 ? mouse : vec2(0.0, 0.4);
    vec2 c = vec2(0.3, -0.4);

    // Parameter bounds
    float tmin = 0.5 + 0.5 * sin(iTime - 0.8);
    float tmax = 0.5 + 0.5 * sin(iTime + 0.8);

    // Ends the of the segment
    vec2 e1 = posBezier(a, b, c, tmin);
    vec2 e2 = posBezier(a, b, c, tmax);

    // Point moving along the bezier and the basis matrix at that point
    float t = mix(tmin, tmax, 0.5 + 0.5 * sin(iTime * 2.0));
    vec2 p = posBezier(a, b, c, t);
    mat2 basis = basisBezier(a, b, c, t);

    // Order smallest to biggest
    if (tmax < tmin) {
        float tmp = tmax;
        tmax = tmin;
        tmin = tmp;
    }

    // Distance
    float d = mapBezier(uv, a, b, c, tmin, tmax, true).x;

    // Nice colors from iq
    vec3 color = 1.0 - sign(d) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-4.0 * abs(d));
	color *= 0.8 + 0.2 * cos(140.0 * d);

    // Fade to and from a texture mapped to the curve's arclength
    vec2 bez = mapBezier(uv, a, b, c, tmin, tmax, false);
    vec2 bUv = vec2(arclenBezier(a, b, c, 0.0, bez.y), bez.x);
    color = mix(color, texture(iChannel0, bUv).rgb, 0.5 - 0.5 * cos(1.5 * iTime));

    // Lines between control points
    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, sdLine(uv, a, b) - 0.002));
    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, sdLine(uv, b, c) - 0.002));

    // Parabola conversion
    vec4 parabola = bezierToParabola(a, b, c);
    vec2 cs = vec2(cos(parabola.z), sin(parabola.z));
    float parabDist = sdParabola((uv - parabola.xy) * mat2(cs, -cs.y, cs.x), parabola.w);
    color = mix(color, vec3(0.0, 1.0, 1.0), smoothstep(unit, 0.0, abs(parabDist) - 0.001) * mod(floor(bUv.x * 20.0), 2.0));
    color = mix(color, vec3(0.0), smoothstep(unit, 0.0, length(uv - parabola.xy) - 0.015)); // Vertex

    // Bezier curve
	color = mix(color, vec3(1.0), smoothstep(unit, 0.0, abs(d) - 0.002));

    // End points
    color = mix(color, vec3(1.0, 0.5, 0.0), smoothstep(unit, 0.0, length(uv - e1) - 0.015));
    color = mix(color, vec3(1.0, 0.5, 0.0), smoothstep(unit, 0.0, length(uv - e2) - 0.015));

    // Basis system axes at the moving point
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, sdLine(uv, p, p + 0.15 * basis[0]) - 0.002));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, sdLine(uv, p, p + 0.15 * basis[1]) - 0.002));

    // Point moving along the bezier
    color = mix(color, vec3(0.5, 0.0, 1.0), smoothstep(unit, 0.0, length(uv - p) - 0.015));

    // Control points
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - a) - 0.015));
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, length(uv - b) - 0.015));
    color = mix(color, vec3(1.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - c) - 0.015));

    // Output arclength
    float arclen = arclenBezier(a, b, c, tmin, tmax);
    color = digitIn(color, vec3(1.0), uv - vec2(-0.8,  -0.4), 100.0, arclen);

    fragColor = vec4(color, 1.0);
}

