#define LIMITED_BY    0    // 0: infinite;  1: circle;  2: square
#define COLOR         1
#define MESH          1
#define MESH_COLOR    1
#define SCALE         1.
#define MAXDIST       10.
#define STEP          0.1

vec3 col(float x, float side) {
#if COLOR
  return vec3(clamp(x/2.+0.5,0.,1.), side, clamp(0.5-x/2.,0.,1.));
#else
  return vec3(clamp(1.-side, 0., 0.8));
#endif
}

float h(vec2 p, float t) {
    return sin(iTime*10. + tanh(iTime/10.)*10./pow(length(p.xy), 0.5))/length(p.xy);
}

mat2 rot(float a) { float c = cos(a), s = sin(a); return mat2(c, s, -s, c); }
//#define rot(a)       mat2(cos(a+vec4(0,11,33,0)))             // rotation hack                  

#define PI       3.1415926
#define R        iResolution
#define H(pos)   h((pos)*SCALE, iTime)/5.
#define INTERP_MIX(PREV, TARGET, CUR)   (TARGET-PREV)/(CUR-PREV)

void mainImage( out vec4 O, in vec2 I )
{
    vec3 D = normalize(vec3(I+I, -4.*R.y) - vec3(R.xy, 0.));    // ray direction
    vec3 p = vec3(0,0,7);                                       // current ray pos

    mat2 rot_yz, rot_xy;            // rotations
    if (iMouse.x < 0.5) {
      rot_yz = rot(-PI*(0.35 - (sin(iTime/6.)+1.)*0.06));
      rot_xy = rot(PI/3. + iTime/10.);
    } else {
      vec2 M = (iMouse.xy / R.xy * vec2(1., -0.5) + vec2(0.5, 0.)) * 2. * PI;
      rot_yz = rot(M.y);
      rot_xy = rot(M.x);
    }
    p.yz *= rot_yz;  p.xy *= rot_xy;
    D.yz *= rot_yz;  D.xy *= rot_xy;

    // Walk the ray
    float height, diff;
    float heightprev = H(p.xy), diffprev = 0.;
    vec3 pprev = p;
    float dist, stp = STEP;      // distance traveled, step
    for (dist = 0.;  dist < MAXDIST;  dist += stp, p += stp*D) {
        height = H(p.xy);
        diff = p.z - height;
        if (
#if LIMITED_BY == 1
            length(p.xy) < 2. &&
#elif LIMITED_BY == 2
            abs(p.x) < 2. && abs(p.y) < 2. &&
#endif
            diff * diffprev < 0.)
        {
            // Crossed the surface
            break;
        }
        heightprev = height;
        pprev = p;
        diffprev = diff;
    }

    if (dist < MAXDIST) {    // Crossed the surface
        // interpolate the exact intersection pos
        float interp = INTERP_MIX(diffprev, 0., diff);
        vec3 pexact = mix(pprev, p, interp);
        float heightexact = H(pexact.xy);

        // sign: +1 = looking top-down, -1 = looking down-up
        float sgn = sign(diffprev);

        vec3 c = col(heightexact*2., (-sgn+1.)/4.) * (1.-(1. - sgn)/3.);
#if MESH
        //vec2 mesh = fract(pexact.xy*5.);
        float mesh_thick = 0.015;      // 0.5 max
        float mesh_density = 3.;      // 0.5 max
        vec2 mesh = max(vec2(0), abs(fract(pexact.xy*mesh_density)-vec2(0.5)) - vec2(0.5*(1.-2.*mesh_thick)))/mesh_thick;
        c = mix(c, vec3(MESH_COLOR), max(mesh.x, mesh.y));
#endif

        // Angle for shading
        //float surface_angle = atan((heightexact - H((pexact.xy+vec2(0.001)))) / 0.0014);
        vec2 shade = vec2(1,0) * rot_xy;
        float surface_angle = atan((heightexact - H((pexact.xy + shade*0.001))) / 0.001);
        c += sgn*(surface_angle/3.);

        O = vec4(c * (MAXDIST - dist)/MAXDIST * 2., 0.);
    } else {
        O = vec4(0);
    }
}
