float _seed;
float rand()
{
    _seed++;
    return hash11(_seed);
}
vec3 rdrtrunk(vec2 uv)
{
    vec3 col = vec3(.75);

    col = mix(col, vec3(.7), 1.-pow(sat(length((uv-vec2(0.,-.1))*3.)),.5));
    float shp = iResolution.x*.5;
    float th = .01;
    float trunk = udSegment(uv, vec2(0.,-.1), vec2(0.,.1))-th;
    trunk = min(trunk, udSegment(uv, vec2(0.,-0.03), vec2(.1,.07)*.5)-th*.7);
    trunk = min(trunk, udSegment(uv, vec2(0.,0.02), vec2(-.1,.1+0.07)*.5)-th*.7);
    vec3 trunkcol = mix(vec3(0.094,0.549,0.122), vec3(0.745,1.000,0.361), 1.-sat(-uv.y*7.+.5))*.7;
    col = mix(col, trunkcol, 1.-sat(trunk*shp));

    return col;
}

vec3 rdr(vec2 uv)
{
    vec2 ouv = uv;
    float shp = iResolution.x*.5;
    vec3 col = rdrtrunk(uv);
    uv -= vec2(0.,.05);
    uv.x /= sat(uv.y)+.9;
    float leave = length(uv)-.1;

    vec2 off = (vec2(rand(), rand())-.5)*.02;
    vec3 rgbleave = rdrtrunk(ouv+off);
    vec3 leavecol = mix(vec3(0.765,1.000,0.639), vec3(0.357,0.851,0.349), 1.-sat(ouv.y*5.));

    col = mix(col, rgbleave*.6+leavecol*.6, 1.-sat(leave*shp));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.xx;
    _seed = iTime+texture(iChannel1, uv).x;
    vec3 col = rdr(uv);
    if (iFrame > 1)
        col = mix(col, texture(iChannel0, fragCoord/iResolution.xy).xyz, .9);
    fragColor = vec4(col,1.0);
}
