void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 uu = texture(iChannel0,uv+vec2(2.,0.)/iResolution.xy);
    vec4 u = texture(iChannel0,uv+vec2(1.,0.)/iResolution.xy);
    vec4 c = texture(iChannel0,uv+vec2(0.,0.)/iResolution.xy);
    vec4 d = texture(iChannel0,uv+vec2(-1.,0.)/iResolution.xy);
    vec4 dd = texture(iChannel0,uv+vec2(-2.,0.)/iResolution.xy);
    vec4 ll = texture(iChannel0,uv+vec2(0.,2.)/iResolution.xy);
    vec4 l = texture(iChannel0,uv+vec2(0.,1.)/iResolution.xy);
    vec4 r = texture(iChannel0,uv+vec2(0.,-1.)/iResolution.xy);
    vec4 rr = texture(iChannel0,uv+vec2(0.,-2.)/iResolution.xy);
    vec4 ul = texture(iChannel0,uv+vec2(1.,1.)/iResolution.xy);
    vec4 ur = texture(iChannel0,uv+vec2(1.,-1.)/iResolution.xy);
    vec4 dl = texture(iChannel0,uv+vec2(-1.,1.)/iResolution.xy);
    vec4 dr = texture(iChannel0,uv+vec2(-1.,-1.)/iResolution.xy);

    vec4 res = c;
    //res -= 0.01*f(c);
    res += 0.01*(f(u)+f(d)-4.*f(c)+f(l)+f(r));
    res -= 0.02*(20.*c+(uu+dd+ll+rr)+2.0*(ul+ur+dl+dr)-8.0*(u+d+l+r));
    //res += 0.05*cos(0.3*iTime)*(2.*d-c-u)+0.05*cos(iTime)*(2.*r-c-l);
    //res += cos(iTime)*0.05*(d-u)-0.05*(r*r-l*l);
    res += cos(iTime)*0.00*(d-u)-0.01*(r*(r.x+r.y+r.z)-l*(l.x+l.y+l.z));
    if(iMouse.z>0.){
        vec4 new = texture(iChannel3,uv);
        res = 0.99*res+0.01*new;
        //res = new;
    }
    if(iFrame<50){
        vec4 new = texture(iChannel3,uv);
        res = new;
        //res = new;
    }
    fragColor = vec4(res.xyz,1.0);
}
