mat2 rot(in float r)
{
	float cr = cos(r);
	float sr = sin(r);
	return mat2(
		cr,-sr,
		sr,cr
	);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 uv = fragCoord/iResolution.xy;
       vec2 st = uv;
     uv.x *= iResolution.x/iResolution.y;

    vec2 center = vec2(0.5+.47,0.5);
    uv-=center;            
    uv *= rot(iTime*0.5);

    vec4 text = texture(iChannel0, st);

    //Get controls
    float r = iMouse.y/iResolution.y;
    float v = iMouse.x/iResolution.x;
    r = clamp(r, 0.,0.249);
    v = clamp(v, 0.249,1.);
    uv*=20.; //Change count here
    uv = fract(uv);

    float blind = smoothstep(v+r,v+0.5-r,uv.x) - 1. + smoothstep(1.-r-v,.5+r-v,uv.x);  
    text = text -1. *-blind;



    fragColor = vec4(text);
}
