#define rot(a)   mat2(cos(a+vec4(0,11,33,0)))         // rotation

float line(vec3 p, vec3 a,vec3 b) { 
    p -= a, b -= a;
    return length(p - b * clamp(dot(p, b) / dot(b, b), 0., 1.));
}

#define limb(a,z)                                                       /* leg */   \
        A = vec3(0,1.5,z.3), B = vec3(.3*cos(a+2.*iTime+vec2(0,11)),z.3), l = length(L=(B-A)/2.), \
        K = (A+B)/2. + vec3(L.y,-L.x,0)*sqrt(1.-l*l)/l,                             \
        t = min(t, min( min(line(q,A,K) -.15, line(q,K,B) -.1 ) , line(q,B,B-vec3(.4,0,0))-.07 ) ), \
        L = vec3( .65*sin( .3*sin(a+2.*iTime)+vec2(0,11)), z.5 ),       /* arm */   \
        B = .8* normalize(vec3(-.5,-L.y-1.1,0)),                                    \
        t = min(t, min( line(q, A+1.1*Y,A+Y+L )-.1 , line(q,A+Y+L,A+Y+L+B) -.07 ) )


void mainImage(out vec4 O, vec2 U)
{
    float t=9., r,x,y,z,l,a=.4; 
    vec3  R = iResolution, d, A,B,K,L, Y=vec3(0,1,0),
          D = normalize(vec3((U+U-R.xy)/R.y, -3.)),       // ray direction
          p = vec3(0,0,14), q,                            // marching point along ray 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(.05*iTime,.1*sin(.5*iTime)+.05,0); 

    for ( O=vec4(1); O.x > 0. && t > .005; O-=.005 )
        q = p,
        q.yz *= rot(.5-6.3*M.y),                          // rotations
        q.xz *= rot(-6.3*M.x),
        t = length(vec2(length(q.xy)-1.,q.z)) -.1,        // wheel
        limb(,), limb(3.14, - ),                          // limbs
        t = min(t, line(q, A,vec3(A.xy,-A.z))-.2),        // pelvis
        A.z=0., t = min(t, line(q, A+.1*Y,A+Y)-.3),       // body
        t = min(t, length((q-A-1.8*Y)/vec3(1,1.5,1))-.3), // head
        A.x+=.1,t = min(t, line(q, A+Y,A+1.5*Y) -.1 ),    // neck
        q.x = abs(q.x), t = min(t, line(q, vec3(.2,-1.05,0), vec3(10,0,0)) -.01 ), // cable
        p += .25*t*D;                                     // step forward = dist to obj
 // O *= 1.5*abs(O);
    if (O.x<0.) O = texture(iChannel0,q);                 // background
}
