// cylindrical variant of https://shadertoy.com/view/flyGDw

#define H(v) fract(1e4*sin((v)*mat2(R,R-71.)))
#define S(v) smoothstep(30./R.y,0.,v)

#define CYL

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, P,D,_P,
          U = 10.* ( u+u - R ) / R.y, U0=U;
    float m = 9.,d, t = iTime, k=8.;
 #ifdef CYL
    U = k*vec2( .995*atan(U.y,U.x), 8./length(U) );        // go to cylinder space
    U.y += t;                                              // animation
 #endif

    for ( int k=0; k < 9; k++ )                            // jittered point grid distribution
        D = vec2(k%3,k/3), P = D - H(mod(floor(U)+D,25.)), // NB: cyclical hash
        d = length(P-fract(U)), 
        d < m ? _P=P, m = d : m;

    P = _P + floor(U);
 #ifdef CYL
    U = U0;
    P = k*8./(P.y-t) * cos( P.x/.995/k + vec2(0,-1.57) ); // back to screen space
    m = length(P-U);
 #endif   
                                                          // draw disk - dark disk
    O = vec4( clamp( S(m-.4) - S( length(P-U+ U*length(U)/1e3 ) -.4 )  ,0.,1.) );
}
