// shape bounded variant of https://shadertoy.com/view/NtV3zV
// connecting components in https://shadertoy.com/view/NlGGRV
// more accelerated variant of https://shadertoy.com/view/stKGzG
// accelerated variant of https://shadertoy.com/view/7ty3zG

#define hue(v)  (v==.5?vec4(0): vec4( .6 + .6 * cos( .5+(v)  + vec3(0,23,21)  ) , 1 ))

// #define length(v) ( abs(v.x)+abs(v.y) ) // variant with straight lines

void mainImage( out vec4 O, vec2 u )
{
 // O = T(u).agbr; return;
    vec2 U = N * u / R.y + 0.*iTime, I = floor(U), F = fract(U);

    O = T(I);                                          // cell states 
    O.w > 0. ? F = vec2(1.-F.y,F.x) : F;

    O =   smoothstep(N*1.5/R.y,0.,abs(min(length(F),length(1.-F))-.5) ) // truchet drawing
        * hue( O[ F.x+F.y < 1. ? 1 : 2 ] );            // coloring

 // if (iMouse.z>0.) O += (1.-O.w)*.3*mod(I.x+I.y,2.); // debug: draw tiles
 // O += .3*T(I).w;
}

#define H(p)         fract(sin(dot(p, vec2(12.9898, 78.233))-iTime) * 43758.5453)
#define keyDown(c) ( texelFetch(iChannel3,ivec2(c,1),0).x > 0.)

#define mask         ( length(P-vec2(.6,0))  < .9 && length( P-vec2(.2,0) ) > .7 )   \
                  || ( length(P+vec2(.8,0))  < .9 && length( P+vec2(.7,.4) ) > .4 )
#define set_Id       { O.x = 1.; O.yz = mask ? (1.+U.x+4096.*U.y)*vec2(1,-1) : vec2(.5) ; }
                  // yz: id of tile segment #1 and #2.  x=1: each time you want to forbid optim on next frame

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    float Nx = floor(N*R.x/R.y);
    vec2 P = ( 2.*U - vec2(Nx,N) ) / N;
    if ( iFrame < 1 || keyDown(32) ) {        // --- init: create truchet
        vec2 I = U;                           // store truchet data
        O-=O;
        O.w = mask    
               ? O.w = step(.5,H(I))          // random axis
               : mod(I.x+I.y,2.);
        set_Id;                               // set id of tile segment #1 and #2
        return;
    }

    O = T(U);                                 // previous state
    vec4 T;
    if (U.x <= Nx && U.y >= N+4. && U.y <= N+N+4.) // --- keep previous version
      { U.y -= N+4.; O = T(U); return; }

    if (U.x >= Nx+4. && U.x <= Nx+4.+Nx/8. && U.y <= N/8.) { // --- reduction map of change
        O-=O;
        U.x -= Nx+4.;
        for (int k=0; k < 64; k++ ) {
            vec2 V = 8.*U + vec2(k%8,k/8),
                 o = T(V + vec2(0.,N+4.)).yz, // old vs new value
                 n = T(V).yz; if ( T(V).x!=0.) continue;
         // if ( o != n ) O = vec4(o,n);      // if changed, store ( why break worse ? )
         // if ( o != n ) { O = vec4(o,n); break; } // why worse ? 
            if ( o.x != n.x ) O.xz = vec2(o.x,n.x); // if changed, store 
            if ( o.y != n.y ) O.yw = vec2(o.y,n.y);
            if (O.x*O.y!=0.) break;
        }
        return;
    }
    if (U.x > Nx || U.y > N) return;          // propagate only visible tiles

                                              // --- propagate id along connections
#define Tc(U)      T(mod(U,vec2(Nx,N)))
#define left       Tc(U+vec2(1,0)).y
#define right      Tc(U-vec2(1,0)).z
#define bot      ( T = Tc(U+vec2(0,1)), T.w<1. ? T.y : T.z )
#define top      ( T = Tc(U-vec2(0,1)), T.w<1. ? T.z : T.y )
    if (O.w<1.) { // tile contains:  \     new id = min(connections)
        O.y = min( O.y, min( right, top ));
        O.z = min( O.z, min( left , bot ));
    }
    else {        // tile contains:  /     new id = min(connections)
        O.y = min( O.y, min( right, bot ));
        O.z = min( O.z, min( left , top ));
    }

  if (T().x > 0.||O.x>0.) {  O.x-- ; return; }// timer: let re-converge before optime
  if ( O.y==.5 || O.z==.5 ) return;           // don't touch outside tiles           

                  // --- shortcut2: at color change, update the color source pixel
    for( float j = 0.; j < N/8.; j++)
        for( float i = 0.; i < Nx/8.; i++) {
            T = T( vec2(Nx+4.+i,j) );
            vec2 o = T.xy, n = T.zw;          // changed old->new value
            if (O.y==o.x) O.y = n.x, O.x=1.;  // oldvalue was my seg1 : update
            if (O.y==o.y) O.y = n.y, O.x=1.;
            if (O.z==o.x) O.z = n.x, O.x=1.;  // oldvalue was my seg2 : update
            if (O.z==o.y) O.z = n.y, O.x=1.;
        }

//if ( O.y==.5 || O.z==.5 ) return;           // don't touch outside tiles           

  if (keyDown(64+18))  set_Id; // key 'R' to reset ids
  if (iMouse.w>0. && U == floor(iMouse.xy/R.y*N) && O.y!=O.z ) O.w = 1.-O.w; // mouse click for swap
  if (U == vec2(0) && ( keyDown(64+18) || iMouse.w>0. ) ) O.x = 1.; // let re-converge 
//if (T().x > 0.||O.x>0.) {  O.x-- ; return; }     // timer: let re-converge befor optim
//return;
                 // --- connect them all : at block connection, swap
    if ( iFrame%10==9 && O.y != O.z && H(U) >.995 ) // should be done after convergence, and not on parallel on same block
        O.w = 1.-O.w, O.x=1.;
}
