vec4 getV(in vec2 p,float max_pos,float max_posy){
    if (p.x < 0.001 || p.y < 0.001) return vec4(0);
    if(ivec2(p)==ivec2(iResolution.xy)-1) return vec4(0);
    vec4 tval=texelFetch( iChannel0, ivec2(p), 0 );
    float p1=decodeval_pos(floatBitsToUint(tval.x))*max_pos;
    float p2=decodeval_pos(floatBitsToUint(tval.y))*max_posy;
    float v1=decodeval_vel(floatBitsToUint(tval.z));
    float v2=decodeval_vel(floatBitsToUint(tval.w));
    return vec4(p1,p2,v1,v2);
}

uvec2 extra_dat_pos(vec2 p){
    vec4 tval=texelFetch( iChannel0, ivec2(p), 0 );
    return uvec2(floatBitsToUint(tval.x)&0xfffu,floatBitsToUint(tval.y)&0xfffu);
}

uvec2 extra_dat_vel(vec2 p){
    vec4 tval=texelFetch( iChannel0, ivec2(p), 0 );
    return uvec2(floatBitsToUint(tval.z)&0xffu,floatBitsToUint(tval.w)&0xffu);
}

// get saved unique ID
uvec2 get_id(vec2 p){
    uvec2 v1=extra_dat_pos(p);
    uint iret=(v1[0]<<12)|(v1[1]<<0);
    return uvec2(iret&0xfffffu,iret>>20);
}

uint get_timer(vec2 p){
    uvec2 v1=extra_dat_vel(p);
    uint iret=(v1[0]<<8)|(v1[1]<<0);
    return iret;
}

uvec2 save_id(uint id, uint color_id){
    id=id&0xfffffu;
    id+=color_id<<20;
    uint a=(id>>12)&0xfffu;
    uint b=(id>>0)&0xfffu;
    return uvec2(a,b);
}

uvec2 save_timer(uint timer){
    uint a=(timer>>8)&0xffu;
    uint b=(timer>>0)&0xffu;
    return uvec2(a,b);
}

bool destr(int id, int max_pos){
    int sx=id%max_pos;
    int sy=id/max_pos;
    return particle_data(ivec2(sx,sy)).w<-0.5;
}

vec2 pack_pos(vec2 pos,uvec2 extra_val, float max_pos, float max_posy){
    uint p1=uint((pos.x/max_pos)*float(0xfffff));
    uint p2=uint((pos.y/max_posy)*float(0xfffff));
    float px=uintBitsToFloat(encodeval_pos(uvec2(p1,extra_val.x)));
    float py=uintBitsToFloat(encodeval_pos(uvec2(p2,extra_val.y)));
    return vec2(px,py);
}

vec2 pack_vel(vec2 vel,uvec2 extra_val){
    float vx=uintBitsToFloat(encodeval_vel(vel.x,extra_val.x));
    float vy=uintBitsToFloat(encodeval_vel(vel.y,extra_val.y));
    return vec2(vx,vy);
}

// save everything to pixel color
vec4 save_all(vec2 pos, vec2 vel, uint id, uint timer, uint color_id){
    uvec2 extra_data_pos_tid=save_id(id, color_id);
    uvec2 extra_data_vel_tid=save_timer(timer);
    float max_pos=iResolution.x;
    float max_posy=iResolution.y;
    vec2 pos_ret=pack_pos(pos,extra_data_pos_tid,max_pos,max_posy);
    vec2 vel_ret=pack_vel(vel,extra_data_vel_tid);
    return vec4(pos_ret,vel_ret);
}

