vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 get_pal(int cid){
    //return vec3(1.);
    float ix = fract(float(cid)/float(0xf)+1./16.);
    vec3 ret = vec3(0.051);
    //vec3 tpal=pal( ix, vec3(0.8,0.5,0.4),vec3(0.2,0.4,0.2),vec3(2.0,1.0,1.0),vec3(0.0,0.25,0.25) );
    vec3 tpal=pal( ix, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(2.0,1.0,0.0),vec3(0.5,0.20,0.25) );
    tpal=clamp(tpal,0.,1.);
    vec3 s = sign(max(tpal-0.1,vec3(0.)));
    ret+=tpal+vec3(0.127)*s+vec3(0.127)*(1.-s);
    return 1.85*ret;
}

