float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);
    return length(p - b * h);
}

#define P       vec3(cos(t),sin(t),.2*t)           // helicoid
#define proj(P) P * mat2x3(cos(a),0,-sin(a),0,1,0) // projection

void mainImage( out vec4 O, vec2 u )
{
    float d = 9.,z,_z,Z=1.1, t, l,_l, 
          a = length(iMouse.xy)>10. ? 6.3*iMouse.x/iResolution.x : iTime;
    vec3 p = vec3(sin(a),0,cos(a));
    vec2 R = iResolution.xy, B, A, 
         U = ( 3.*u - R ) / R.y -.4;
    O-=O;     
    Z = 99.;   // --- compute dist to wall. analytic estimate instead ?
    for( t = -8.; t < 8.; t+=.02) Z = min(Z,dot(p,P));
    Z = -Z + .05;
    for( t=-8.,A = proj(P); t < 8.; t+=.02) { // --- draw shadow projection
        B = A, A = proj(P);
        z = ( Z + dot(p,P) )/9.; 
        l = line(U,B,A);
   //  _l = line(U-.2,B,A); if ( _l < d ) d=_l, _z=z; // if draw shape
        l /= z;// O = max(O,  .2*exp(-.5*l*l) / sqrt(6.28*z*z) );
                  O += .006 * exp(-.5*l*l) / sqrt(6.28*z*z);
    }

    O = 1.-O;
  //O = mix( clamp(O,0.,1.), vec4(0,cos(50.*d),0,0), smoothstep(5./R.y,0.,d-.02) ); // show shape
}
