// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}
vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}

vec3 ColumnaX(vec3 p)
{   vec3 res= vec3(9999.0, -1.0,-1.0);
    vec3 p0=p;
    vec3 p1,p2;
    p1= rotate_z(p0-vec3(-6.0,4.5,-12.0), radians(20.0));
    p2= rotate_z(p0-vec3(-6.0,4.5,-12.0), radians(-20.0));
    float d1a =sdCylinderXZ(p1, vec2(0.65,4.0) );
    float d1b =sdCylinderXZ(p2, vec2(0.65,4.0) );
    float d0b= sdBox(p0-vec3(-6.0,8.5,-12.0),vec3(2.0,0.25,2.0) );
    res =opU3(res, vec3(d1a,9.0,-1.0));
    res =opU3(res, vec3(d1b,9.0,-1.0));
    res =opU3(res, vec3(d0b,9.0,-1.0));
    return res;
}

vec3 techoInclinado(vec3 p)
{  vec3 res= vec3(9999.0, -1.0,-1.0);
    vec3 p0=p;
    vec3 p1, p2;
    p1= rotate_z(p0-vec3(-5.0,11.0,-7.0), radians(-30.0));
    p2= rotate_z(p0-vec3( 5.0,11.0,-7.0), radians( 30.0));
    float d0a= sdBox(p1,vec3(6.0,0.25,7.0) );
    float d0b= sdBox(p2,vec3(6.0,0.25,7.0) );
    res =opU3(res, vec3(d0a,9.0,-1.0));
    res =opU3(res, vec3(d0b,9.0,-1.0));   
    return res;
}


vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 p0=p;
	float planeDist1 = p.y+0.0;  //piso inf

   res =opU3(res, vec3(planeDist1,-1.0,5.0));
    float d0a= sdBox(p-vec3(0.0,0.0,-4.0),vec3(10.0,1.0,10.0) );
    res =opU3(res, vec3(d0a,-1.0,7.0));
    p.x=abs(p.x)-12.5;
    vec3 resc1=ColumnaX(p);
    vec3 resc2=ColumnaX(p-vec3(0.0,0.0,5.0));
    vec3 resc3=ColumnaX(p-vec3(0.0,0.0,10.0));

    p=p0;
    vec3 rest1= techoInclinado(p);

    res =opU3(res,resc1);
    res =opU3(res,resc2);
    res =opU3(res,resc3);

    res =opU3(res,rest1);

    return res;
}

vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;

    #define DISTANCE_BIAS 0.75
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}


float occlusion(vec3 pos, vec3 nor)
{
    float sca = 2.0, occ = 0.0;
    for(int i = 0; i < 10; i++) {

        float hr = 0.01 + float(i) * 0.5 / 4.0;        
        float dd = GetDist(nor * hr + pos).x;
        occ += (hr - dd)*sca;
        sca *= 0.6;
    }
    return clamp( 1.0 - occ, 0.0, 1.0 );    
}

vec3 lightingv3(vec3 normal,vec3 p, vec3 lp, vec3 rd, vec3 ro,vec3 col, float t) 
{   vec3 lightPos=lp;
    vec3 hit = ro + rd * t;
    vec3 norm = GetNormal(hit);
    vec3 light = lightPos - hit;
    float lightDist = max(length(light), .001);
    float atten = 1. / (1.0 + lightDist * 0.125 + lightDist * lightDist * .05);
    light /= lightDist;
    float occ = occlusion(hit, norm); 
    float dif = clamp(dot(norm, light), 0.0, 1.0);
    dif = pow(dif, 4.) * 2.;
    float spe = pow(max(dot(reflect(-light, norm), -rd), 0.), 8.);
    vec3 color = col*(dif+.35 +vec3(.35,.45,.5)*spe) + vec3(.7,.9,1)*spe*spe;
    color*=occ;
    return color;
}

