    {
    p=1.570796*vec4(440.,520.,691 ,4902.);
    if(mod(rt, 10.)> 8.) p=1.570796*vec4(1440., 1890, 603, 2685);
    }

    f =  sin(p[int(6. * t)%4]*rt);
    f*= (min(1.,3.*ft)*max(0.,1.-ft));
    f*= cos( _f(.1*t)  + 10. * exp(-.205 * pow(1. - ft , 10.)));
