void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float off = .002;
    // Time varying pixel color
    vec3 d = texture(iChannel1,uv).rgb;
    vec3 du = d-texture(iChannel1,uv-vec2(0.,off)).rgb;
    vec3 dd = d-texture(iChannel1,uv-vec2(0.,-off)).rgb;
    vec3 dr = d-texture(iChannel1,uv-vec2(off,0.)).rgb;
    vec3 dl = d-texture(iChannel1,uv-vec2(-off,.0)).rgb;
    d = (du+dd+dr+dl) * vec3(1.,0,0) * smoothstep(40.,30.,d);

    vec3 n = texture(iChannel2,uv).rgb;
    float nu = dot(n,texture(iChannel2,uv-vec2(0.,off)).rgb);
    float nd = dot(n,texture(iChannel2,uv-vec2(0.,-off)).rgb);
    float nr = dot(n,texture(iChannel2,uv-vec2(off,0.)).rgb);
    float nl = dot(n,texture(iChannel2,uv-vec2(-off,.0)).rgb);
    n=1.-vec3(min(min(min(nu,nd),nr),nl));
    n=clamp(n,0.,1.);
    n*=vec3(1,0,0);
    vec3 col = texture(iChannel3,uv).rgb;

    // Output to screen
    fragColor = vec4(d+n+col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURFACE_DIST 0.01

vec2 rand2(vec2 p)
{
	vec2 q = vec2(dot(p,vec2(127.1,311.7)), 
		dot(p,vec2(269.5,183.3)));
	return fract(sin(q)*43758.5453);
}

float rand(vec2 p)
{
	return fract(sin(dot(p,vec2(419.2,371.9))) * 833458.57832);
}

float iqnoise(in vec2 pos, float irregular, float smoothness)
{
	vec2 cell = floor(pos);
	vec2 cellOffset = fract(pos);

	float sharpness = 1.0 + 63.0 * pow(1.0-smoothness, 4.0);

	float value = 0.0;
	float accum = 0.0;
	// Sample the surrounding cells, from -2 to +2
	// This is necessary for the smoothing as well as the irregular grid.
	for(int x=-2; x<=2; x++ )
	for(int y=-2; y<=2; y++ )
	{
		vec2 samplePos = vec2(float(y), float(x));

  		// Center of the cell is not at the center of the block for irregular noise.
  		// Note that all the coordinates are in "block"-space, 0 is the current block, 1 is one block further, etc
		vec2 center = rand2(cell + samplePos) * irregular;
		float centerDistance = length(samplePos - cellOffset + center);

		// High sharpness = Only extreme values = Hard borders = 64
		// Low sharpness = No extreme values = Soft borders = 1
		float sam = pow(1.0 - smoothstep(0.0, 1.414, centerDistance), sharpness);

		// A different "color" (shade of gray) for each cell
		float color = rand(cell + samplePos);
		value += color * sam;
		accum += sam;
	}

	return value/accum;
}

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}
float sdCylinder(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = a + t*ab;

    float x = length(p-c)-r;
    float y = (abs(t-.5)-.5)*length(ab);
    float e = length(max(vec2(x, y), 0.));
    float i = min(max(x, y), 0.);

    return e+i;
}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r){
    //line from a to b
    vec3 ab = b-a;

    // line from p to a
    vec3 ap = p-a;

    // as p approaches a, it will be 1
    // as p approaches b it will b 0
    float t = dot(ab,ap) / dot(ab,ab);

    // clamp values between 0 and 1
    t= clamp(t,0.,1.);

    // center point of the capsule
    // start at a (top of capsule)
    // go towards b, t steps
    vec3 c = a + t*ab;

    float d = length(p-c)-r;

    return d;
}

float sdTorus(vec3 p, vec2 r){
    float x = length(p.xz)-r.x;
    return length(vec2(x, p.y))-r.y;
}

float dBox(vec3 p, vec3 s){
    return length(max(abs(p)-s,0.));
}
vec2 colMin(vec2 res, vec2 obj){
    res.x = min(obj.x,res.x);
    if(obj.x == res.x){
        res.y = obj.y;
    }
    return res;
}
vec2 GetDist(vec3 p){
    // some test object
    vec4 sphere = vec4(0,1,6,1);

    // distance to sphere
    //--------------------------------
    // distance to center of sphere: 
    // length(sphere position - camera position)
    // distance to outside of the sphere:
    // length(sphere position - camera position) - sphere radius
    vec2 ds = vec2(length(p-sphere.xyz-vec3(sin(iTime),0.,0))-sphere.w,0.);

    //distance to floor (plane)
    //this is just height of the origin point passed
    vec2 dp = vec2(p.y,1.);

    //capsule distance
    vec2 cd = vec2(sdCapsule(p, vec3(2,0.5,6), vec3(2,1.5,6), 0.5),2.);

    //torus distance
    vec2 td = vec2(sdTorus(p-vec3(-3,0.5,6), vec2(.5,.3)),3.);

    //box distance
    vec2 bd = vec2(dBox(p-vec3(-2,0.5,8),vec3(0.5)),4.);
    p.y-=sin(iTime)*.5+.5;
    p-=vec3(1, .3, 5);
    p.xz*=Rot(iTime);

    // cylinder distance
    vec2 cyld = vec2(sdCylinder(p, vec3(0.), vec3(3, 0, 1), .3),5.);

    //choose the closest of the two
    vec2 d = colMin(ds,dp);
    d = colMin(cd,d);
    d = colMin(d,td);
    d = colMin(bd,d);
    d = colMin(cyld,d);
    return d;
}
vec2 RayMarch(vec3 ro, vec3 rd){
    // distance from origin
    vec2 dO=vec2(0.,0.);
    // march until max steps is achieved or object hit
    for(int i=0; i <MAX_STEPS; i++){
        // current point being evaluated
        vec3 p = ro + dO.x*rd;

        // get distance to seam
        vec2 ds = GetDist(p);
        //move origin to new point
        dO+=ds.x*.8;
        if(ds.x < SURFACE_DIST){
            dO.y = ds.y;
            break;
        }
        else if( dO.x > MAX_DIST){
            dO.y= -1.;
            break;
        }
    }
    return dO;
}
vec3 GetNormal(vec3 p){
    //distance to point being analyzed
    float d = GetDist(p).x;

    //distance to another point along the objects surface that is closeby
    vec2 e = vec2(0.01,0);

    //slope between the two points
    //note: swizzel is the .xxy or .yyx etc
    vec3 n = d - vec3(
         GetDist(p-e.xyy).x,
         GetDist(p-e.yxy).x,
         GetDist(p-e.yyx).x);

    return normalize(n);

}
float GetLight(vec3 p){
    //position of the light source
    vec3 lightPos = vec3(0,5.,3);

    lightPos.xz += vec2(sin(iTime)*5.,cos(iTime)*5.);
    //light vector
    vec3 l = normalize(lightPos-p);

    //normal of object
    vec3 n = GetNormal(p);

    // dot product of the light vector and normal of the point
    // will give us the amount of lighting to apply to the point
    // dot() evaluates to values between -1 and 1, so we will clamp it
    float diff = clamp(dot(n, l),0.,1.);

    // calculate if point should be a shadow:
    // raymarch from point being calculated towards light source
    // if hits surface of something else before the light,
    // then it must be obstructed and thus is a shadow
    // the slight offset "p+n*SURFACE_DIST*1.1" is needed to ensure the
    // break condistions in the function are not met too early
    float d = RayMarch(p+n*SURFACE_DIST*1.1,l).x;
    if(d < length(lightPos-p)){
        diff *= .1;
    }

    return diff;
}
vec3 Render (inout vec3 ro, inout vec3 rd, inout float reflVal){
//------------------
    vec2 d = RayMarch(ro,rd);
// position of point
    // start at ray origin
    // move d units in the ray direction
    vec3 p = ro + rd *d.x;
 //get diffuse lighting
    float diff = GetLight(p);
    vec3 col = vec3(0);
    col = vec3(diff);

    vec3 n = GetNormal(p);
    vec3 r = reflect(rd, n);
    vec3 refl = texture(iChannel0, r).rgb;
    refl= refl*abs(n.y) + refl*abs(n.z) + refl*abs(n.x);

    if(d.y == 0.){
        float noise = iqnoise(p.xz*10.,1.,1.);
        float noise2 = iqnoise(p.xy*10.,1.,1.);
        col = vec3(noise*noise2);

        reflVal = smoothstep(.2,.6,noise*noise2);
    }
    else if(d.y==1.){

       col *= vec3(1,1,0);

       reflVal = 0.9;
    }
    else if(d.y==2.){
       float noise = iqnoise(p.xy*10.,1.,1.);
       col *= noise*vec3(1,0,1);
       reflVal = smoothstep(.3,.7,noise);
    }
    else if(d.y==3.){
      float noise = iqnoise(p.xz*10.,1.,1.);
      col *= noise*vec3(.3,.3,1);
      reflVal = smoothstep(.3,.7,noise);
    }
    else if(d.y==4.){
      float noise = iqnoise(p.xz*10.,1.,1.);
      float noise2 = iqnoise(p.xy*10.,1.,1.);
      col *= noise2*noise*vec3(1,.3,.4);
      reflVal=smoothstep(.2,.6,noise*noise2);;
    }
    else if(d.y==5.){
    float noise = iqnoise(p.xz*10.,1.,1.);
      float noise2 = iqnoise(p.xy*10.,1.,1.);
      col *= noise2*noise*vec3(0,1,1);
      reflVal=smoothstep(.2,.6,noise*noise2);;
    }
    else{
        col=texture(iChannel0, rd).rgb*.3;
    }
     ro = p+n*SURFACE_DIST*3.;
     rd=r;
    return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -0.5 to 0.5)
    // origin is now (0,0)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;


    //camera
    //-----------------
    // ray origin
    //controls position
    vec3 ro = vec3(0,4,0);

    //ray dir
    //controls rotation
    vec3 rd = normalize(vec3(uv.x,uv.y-0.5,1));
    //------------------
    float reflVal = 1.;
    vec3 col = Render(ro,rd,reflVal);
    vec3 bounce = reflVal*Render(ro,rd,reflVal);
    col += bounce;

    fragColor = vec4(col,1.0);
}

