#define PI 3.141592653589

#define COL1 vec3(1.,0.,0.5)
#define COL2 vec3(1.,0.8,0.2)
#define SHADOW vec3(0.)



#define SF 1./min(iResolution.x,iResolution.y)*.5*SCALE
#define SS(l,s) smoothstep(SF,-SF,l-s)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.x;  

    float SCALE = 1.75 + sin(iTime*.5)*.125;

    ouv *= SCALE;       

    vec3 col = vec3(0.);

    for(float ir=0.;ir<2.;ir+=1.){

        vec2 uv = ouv;


        float ca = cos(PI * .25 * ir);
        float sa = sin(PI * .25 * ir);
        mat2 rot = mat2(ca, -sa, sa, ca);
        uv *= rot;        

        // uv = abs(uv);

        for(int i=0;i<2;i+=1){

            float x;
            float y;

            float fr = 16.;

            if(i==0){
                y = uv.x*fr;
                x = uv.y;
            } else {
                y = uv.y*fr;
                x = uv.x;
            }

            float wooble = sin(-iTime*10. + length(uv)*10.)*.25+.5;


            float sf = .05 + wooble*.01 ;   
            float w = .025 + wooble*.01 ;
            float ssFrom=.05;
            float ssTo=.02;

            float l = length(uv);
            float shDraw; 
            if(ir == 0.){
                shDraw = step(.15, l);
            } else {
                shDraw = step(.25, l);
            }  



            float d1v = abs(x + sin(y)*sf);    
            float d1 = SS(d1v, w);
            float d1o = 1. - smoothstep(0., w*2.25, d1v);
            float d1s = smoothstep(ssFrom, ssTo, d1v) * shDraw;

            float d2v = abs(x + sin(y + PI)*sf);   
            float d2 = SS(d2v, w);
            float d2o = 1. - smoothstep(0., w*2.25, d2v);
            float d2s = smoothstep(ssFrom, ssTo, d2v) * shDraw;

            float v = sin(y +  PI*.25);


            if(v > 0.){
                col = mix(col, COL1 * d1o, d1);                
                col = mix(col, SHADOW, d2s); 
                col = mix(col, COL2* d2o, d2);  
            } else {          
                col = mix(col, COL2* d2o, d2);  
                col = mix(col, SHADOW, d1s); 
                col = mix(col, COL1* d1o, d1);  
            }

            col *= SS(.035 + wooble*.015, l);

        }
    }




    fragColor = vec4(col,1.0);
}
