const float PI = 3.14159265359;
float circle(vec2 p, vec2 c, float r){
    float d = 0.5 + iMouse.x / iResolution.x;
    return pow(dot(p - c, p - c), d) - r;
}
vec3 contour(float v, float interval){
    return abs(v) < 0.01 ? vec3(0.0):
    mod(v, interval) < 0.01 ? vec3(1.0):
    mix(vec3(0, 0, 1), vec3(1, 0, 0), atan(v) / PI + 0.5);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 pos = (2.0 * fragCoord.xy -iResolution.xy)/ min(iResolution.x, iResolution.y);
    fragColor.rgb = vec3(contour(circle(pos, vec2(0.0), 1.0), 0.3));
    fragColor.a = 1.0;
}
