// Signed distance and line segment

// Segment:              https://www.shadertoy.com/view/3tdSDj
// and many more here:   https://iquilezles.org/articles/distfunctions2d

float line_segment(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba);
}

vec3 line_color(in float d) {

	vec3 color = vec3(1.) - sign(d) * vec3(0., 0.8, 0.);
	color *= 1.5 - exp(.5 * abs(d));
	color *= .5 + .3 * d;
	color = mix(color, vec3(1.), 1. - smoothstep(.0, .015, abs(d)));

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 pos = (fragCoord - iResolution.xy*0.5)/iResolution.y;
	float zoom = 2.5;
	pos *= zoom;


    // Head
    vec2 headPos = pos+vec2(0.,0.6);
	vec2 headCoord1 = vec2(0.,0.5);
	vec2 headCoord2 = vec2(0.,0.5);
	float headThickness = 0.1;

	float headD = line_segment(headPos, headCoord1, headCoord2) - headThickness;

	vec3 headColor = line_color(headD);


    // Body
    vec2 bodyPos = pos+vec2(0.,0.8);
	vec2 bodyCoord1 = vec2(0.,0.5);
	vec2 bodyCoord2 = vec2(0.,0.);
	float bodyThickness = 0.05;

	float bodyD = line_segment(bodyPos, bodyCoord1, bodyCoord2) - bodyThickness;

	vec3 bodyColor = line_color(bodyD);


    // Left arm
    vec2 leftArmPos = pos+vec2(0.12,0.32);
	vec2 leftArmCoord1 = vec2(0.,0.);
	vec2 leftArmCoord2 = vec2(-0.25,-0.25);
	float leftArmThickness = 0.03;

	float leftArmD = line_segment(leftArmPos, leftArmCoord1, leftArmCoord2) - leftArmThickness;

	vec3 leftArmColor = line_color(leftArmD);


    // Right arm
    vec2 rightArmPos = pos+vec2(-0.12,0.32);
	vec2 rightArmCoord1 = vec2(0.,0.);
	vec2 rightArmCoord2 = vec2(0.02*sin(iTime*3.)+0.20,0.25);
	float rightArmThickness = 0.03;

	float rightArmD = line_segment(rightArmPos, rightArmCoord1, rightArmCoord2) - rightArmThickness;

	vec3 rightArmColor = line_color(rightArmD);


    // Right leg
    vec2 rightLegPos = pos+vec2(-0.08,0.87);
	vec2 rightLegCoord1 = vec2(0.,0.);
	vec2 rightLegCoord2 = vec2(0.05,-0.35);
	float rightLegThickness = 0.03;

	float rightLegD = line_segment(rightLegPos, rightLegCoord1, rightLegCoord2) - rightLegThickness;

	vec3 rightLegColor = line_color(rightLegD);


    // Left leg
    vec2 leftLegPos = pos+vec2(0.05,0.87);
	vec2 leftLegCoord1 = vec2(0.,0.);
	vec2 leftLegCoord2 = vec2(-0.05,-0.35);
	float leftLegThickness = 0.03;

	float leftLegD = line_segment(leftLegPos, leftLegCoord1, leftLegCoord2) - leftLegThickness;

	vec3 leftLegColor = line_color(leftLegD);






    vec3 color = headColor+bodyColor+leftArmColor+rightArmColor+rightLegColor+leftLegColor;

	fragColor = vec4(color, 1.);
}
