#define pi 3.14159

float thc(float a, float b) {
    return tanh(a * cos(b)) / tanh(a);
}

float ths(float a, float b) {
    return tanh(a * sin(b)) / tanh(a);
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21 (vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    float sc = 64.; // change me
    vec2 ipos = floor(sc * uv) + 0.5;
    vec2 fpos = fract(sc * uv) - 0.5;

    float h = h21(ipos);
    float time = 0.2 * h + 0.3 * mlength(fpos) + iTime;

    vec2 p = 0.5 * vec2(thc(3., time), ths(3., time));

    float e = 0.4;
    float d = length(uv / p - e);

    float k = 0.2;
    float s = smoothstep(-k, k, -d + pow(cos(24. * d - iTime),3.) * e);

    vec3 col = s * pal(ths(3., time) + thc(3., time), vec3(1.), vec3(1.), vec3(1.), (0.5/3.) * vec3(0,1,2));

    fragColor = vec4(col,1.);
}
