#define sat(a) clamp(a, 0., 1.)
mat2 r2d(float a){float cosa = cos(a);float sina = sin(a);return mat2(cosa,sina,-sina,cosa);}

float _time;

vec3 getCam(vec3 rd, vec2 uv)
{
    float fov = 1.;
    vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
    vec3 u = normalize(cross(rd, r));
    return normalize(rd+fov*(uv.x*r+-uv.y*u));
}

vec2 _min(vec2 a, vec2 b)
{
    if (a.x < b.x)
        return a;
    return b;
}

float _cucube(vec3 p, vec3 s, vec3 th)
{
    vec3 l = abs(p)-s;
    float cube = max(max(l.x, l.y), l.z);
    l = abs(l)-th;
    float x = max(l.y, l.z);
    float y = max(l.x, l.z);
    float z = max(l.x, l.y);

    return max(min(min(x, y), z), cube);
}

vec2 map(vec3 p)
{
    vec2 acc = vec2(10000.);
    for (int i = 0; i < 15; ++i)
    {
        float fi = float(i);
        vec3 pl = p;
        pl.xy *= r2d(sin(iTime*.5-fi*.1)*2.);   
        //pl.xz *= r2d(fi+iTime*(1.+fi)*.1);
        //p.xz *= r2d(float(i));
        //p *= .8;
        float cucube = _cucube(pl, 5.*vec3(.5, .1, .5)*(fi/15.), vec3(.0001));
        acc = _min(acc, vec2(cucube, float(i)));
    }

    return acc;
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
    vec3 p = ro;
    for (int i = 0; i < steps; ++i)
    {
        vec2 res = map(p);
        if (res.x < 0.01)
            return vec3(res.x, distance(p, ro), res.y);
        p+= rd*res.x;
    }
    vec3(-1.);
}

vec3 getNormal(float d, vec3 p)
{
    vec2 e = vec2(0.001,0.);
    return normalize(vec3(d) - vec3(map(p-e.xyy).x, map(p-e.yxy).x, map(p-e.yyx).x));
}

vec3 grad(float f)
{
    vec3 cols[4];
    cols[0] = vec3(0.05);
    cols[1] = vec3(0.859,0.039,0.286);
    cols[2] = vec3(1.);
    cols[3] = vec3(1.0);


    f = pow(sat(sin(f*3.-iTime*1.5)*.5+.5), 2.)*3.0;
    vec3 prev = cols[int(f)];
    vec3 next = cols[int(min(f+1.,3.))];
    return mix(prev, next, fract(f))*1.2;
}

vec3 rdr(vec2 uv)
{
    float d = 11.;
    vec3 ro = vec3(sin(_time*.25)*d,0.,cos(_time*.25)*d);
    vec3 ta = vec3(0.,0.,0.);
    vec3 rd = normalize(ta-ro);
    rd = getCam(rd, uv);
    vec3 col = texture(iChannel2, rd*vec3(1.,1.,1.)).xxx*.1;
    vec3 res = trace(ro, rd, 64);
    if (res.y > 0.)
    {
        vec3 p = ro + rd*res.y;
        vec3 n = getNormal(res.x, p);

        col = n*.5+.5;
        col = grad(res.z/15.);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    _time = iTime;// .15*iTime+texture(iChannel0, fragCoord/8.).x*iTimeDelta;
    vec2 ouv = fragCoord/iResolution.xy;
    vec2 uv = (fragCoord-vec2(.5)*iResolution.xy)/iResolution.xx;

    vec3 col = rdr(uv);
    //col += sat(grad(1.-sat(length(uv*2.)))*2.)*(1.-sat(length(uv*4.)));
    col = mix(col, texture(iChannel1, ouv).xyz, .5);

    fragColor = vec4(col,1.0);
}

