vec3 line(vec2 pos,vec2 point1,vec2 point2,float width,vec3 color ,float antialias)
{

    float k = (point1.y - point2.y)/(point1.x - point2.x);
    float b = point1.y - k * point1.x;

    float d = abs(k * pos.x - pos.y + b) / sqrt(k * k + 1.0);
    float t = smoothstep(width/2.0-antialias, width/2.0 + antialias, d);
    return vec3(color*(1.0 - t));

}

float random(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }

vec2 stepPoint(float x,float cycle , float scale)
{
    float step =x- mod(x,cycle); 
    return vec2(step, random(step) * scale*0.5 + 0.3);
}

vec3 shakeLine(vec2 uv,float cycle,float width,vec3 color ,float antialias , float scale)
{
    vec2 p1 =stepPoint(uv.x,cycle , scale);
    vec2 p2 =stepPoint(uv.x+cycle,cycle , scale);
    return line(uv,p1,p2,width,color,antialias);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float scale = 1.0- abs( uv.x- 0.5); 
    uv.x =fract(uv.x + iTime*0.2); 


    vec3 l1= shakeLine(uv,0.01 ,0.002,vec3(1,0,0),0.001 , scale);
    vec3 l2= shakeLine(uv,0.05 ,0.002,vec3(0,1,0),0.001 , scale);
    vec3 l3= shakeLine(uv,0.001,0.002,vec3(0,0,1),0.001 , scale);

    vec3 l4 = line(uv ,vec2(0,0.4),vec2(1,0.4),0.002, vec3(1,1,1),0.00);
    vec3 col = l1 + l2 +l3 +l4;
    // Output to screen
    fragColor = vec4(col,1.0);
}
