#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

// RayMarching from TheArtOfCode

// From BlackleMori
#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox(in vec2 p, in vec2 b){
    vec2 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,d.y),0.);
}

vec3 GetRayOrigin() {
    vec2 m = iMouse.xy/iResolution.xy;
    float r = 12. - 8.5 * tanh(0.25 * iTime);
    float a = -0.1 * iTime;
    vec3 ro = vec3(r * cos(a), 0, r * sin(a));
   // ro.yz *= rot(-m.y*3.14+1.);
   // ro.xz *= rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    float oa = atan(p.x, p.z);
    vec3 op = p;
    p.xz = sabs(p.xz) - 2.;
    p.y += 0.1 * cos(4. * p.x + iTime);

    float a = atan(p.x, p.z);
    p.y += 0.1 * cos(3. * oa + iTime);

    //p.y = abs(p.y) - 1.5;
    float r1 = 1.25;
    float r2 = 0.75 + 0.5 * cos(a + iTime);
    float d1 = length(p.xz) - r1;
    float d2 = sdBox(vec2(p.y, d1), vec2(r2,1)) - 0.1;
    return d2;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = GetRayOrigin();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    vec3 p = ro + rd * d;
    float IOR = 1.15 + (0.5 + 0.5*thc(2.,0.4 * iTime))*0.25 * h21(floor(10. * p.yy + 0.5 * iTime));
    //float IOR = 1.15 + 0.5 * h21(floor(10. * p.yy + 2100. * iTime));//0.001 * h21(uv + iTime * 0.01);
    if(d<MAX_DIST) {        
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        float dif2 = dot(nExit, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        float fres = pow(1. + dot(rd, n), 5.);
        float fres2 = pow(1. + dot(rd, nExit), 5.);
        float fog = 1.-cos(-1.2 * .4 * length(pExit)); //was exp

        float spec = pow(dif, 15.);
        float spec2 = pow(dif2, 15.);
        //col += vec3(spec);
        //col = mix(col, vec3(0), 1. - fres);
        vec3 col2 = pal(log(length(pExit)) * 0.5, 0.5 * vec3(0,1,2)/3.);
        col *= col2;
        col = mix(col, vec3(0), 1. - fres2);
        col = mix(col, vec3(spec), 0.5);

        col += col2 * pow(0.5 + 0.5 * cos(50. * log(length(pExit)) - 2. * iTime), 33.);
        //col += spec2 * col2;
        col *= pal(0.8 + log(length(pExit.xz)) + spec2 * 2., 0.5 * vec3(0,1,2)/3.);
       col = mix(col, vec3(1,0.5,0.5), fog);
   }
    //col *= abs(rd) * 0.5 + 0.5;
    col = pow(col, vec3(.4545));	// gamma correction
    col *= 1. + (1.-exp(-0.25 * length(uv))) * .15 * thc(4., 0.75 * iResolution.y * uv.y);
    fragColor = vec4(col,1.0);
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)
#define ths(a,b) tanh(a*sin(b))/tanh(a)
#define sabs(x) sqrt(x*x+1e-2)
//#define sabs(x, k) sqrt(x*x+k)

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))

float cc(float a, float b) {
    float f = thc(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

float cs(float a, float b) {
    float f = ths(a, b);
    return sign(f) * pow(abs(f), 0.25);
}

vec3 pal(in float t, in vec3 d) {
    return 0.5 + 0.5 * cos(2. * pi * (0.5 * t + d));
}

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b * cos(2. * pi * (c * t + d));
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

float mlength(vec2 uv) {
    return max(abs(uv.x), abs(uv.y));
}

float mlength(vec3 uv) {
    return max(max(abs(uv.x), abs(uv.y)), abs(uv.z));
}

float sfloor(float a, float b) {
    return floor(b) + 0.5 + 0.5 * tanh(a * (fract(b) - 0.5)) / tanh(0.5 * a);
}

// From iq, k = 0.12 is good
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) - k * h * (1. - h);
}

float smax(float a, float b, float k) {
    float h = clamp(0.5 - 0.5 * (b - a) / k, 0., 1.);
    return mix(b, a, h) + k * h * (1. - h); 
}
