// variant of https://shadertoy.com/view/7lcyDf

#define rot(a)      mat2(cos(a+vec4(0,11,33,0)))           // rotation 

float L(vec3 p) { 
    p.x += clamp(-p.x, 0., 1.);                            // proj coord on line  
    return length(p);                                      // dist to segment
}
float map(vec3 q) {
    q = abs(q)-1.;
    return min( L(q) , min( L(q.yzx), L(q.zxy) )) - .1;
}

void mainImage(out vec4 O, vec2 U)
{
    float t=9.;
    vec3  R = iResolution, N, e = vec3(.01,0,0),
          D = vec3(U*.4 - R.xy*.2, -R.y) / R.y,            // ray direction
          p = vec3(0,0,10), q, r,                          // marching point along ray 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5
              :  vec3(.1,0,0) * cos(iTime + vec3(0,11,0)); 
    for (O=vec4(1); O.x > 0. && t > .01; O-=.01)           // march scene
        q = p,
        q.yz *= rot(.78-6.*M.y),                           // rotations
        q.xz *= rot(.78-6.*M.x), 
        t = map(q),
     // t = min(t, map(q)),
        p += t*D;                                          // step forward = dist to obj          

   if (t<.01) 
       N = normalize( t - vec3( map(q+e), map(q+e.yxy), map(q+e.yyx) ) ),
       O -= dot(N,vec3(.7)) +O;
}
