void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;
    float x=st.x;
    float y=st.y;
    vec3 color;
    vec3 color1=vec3(1.792,0.053,0.104);
        vec3 color2=vec3(1.000,0.635,0.000);
        vec3 color3=vec3(1.792,1.719,0.000);
        vec3 color4=vec3(0.510,1.792,0.023);
        vec3 color5=vec3(0.005,1.792,1.324);
        vec3 color6=vec3(0.017,0.169,1.792);
        vec3 color7=vec3(1.398,0.000,1.792);

    float x1=step(0.0/7.0,x)-step(1.0/7.0,x);
    float x2=step(1.0/7.0,x)-step(2.0/7.0,x);
    float x3=step(2.0/7.0,x)-step(3.0/7.0,x);
    float x4=step(3.0/7.0,x)-step(4.0/7.0,x);
    float x5=step(4.0/7.0,x)-step(5.0/7.0,x);
    float x6=step(5.0/7.0,x)-step(6.0/7.0,x);
    float x7=step(6.0/7.0,x)-step(7.0/7.0,x);  
    //color=color1*(x1)+color2*x2+color3*x3+color4*x4+color5*x5+color6*x6+color7*x7;
	float rg=smoothstep(0.0,4.0/7.0,x);
    float gb=smoothstep(3.0/7.0,6.0/7.0,x);
    float br=smoothstep(5.0/7.0,1.0,x);
    vec3 color_rg=mix(color1,color4,rg);
    vec3 color_gb=mix(color4,color6,gb);
    vec3 color_br=mix(color6,color7,br);
    vec3 color_rggb=mix(color_rg,color_gb,smoothstep(rg,gb,x));
    vec3 color_gbbr=mix(color_gb,color_br,smoothstep(gb,br,x));
    vec3 color_rgb=mix(color_rggb,color_gbbr,smoothstep(smoothstep(rg,gb,x),smoothstep(gb,br,x),x));
    fragColor = vec4(color_rgb,1.0);
}
