// variant of dipole  https://shadertoy.com/view/Nsj3zR


void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( 2.*u - R ) / R.y,
         M = ( 2.*iMouse.xy  - R ) / R.y,
        J0 = vec2(  1.,  0. ),
        J1 = vec2( -.5,  sqrt(3.)/2. ),
        J2 = vec2( -.5, -sqrt(3.)/2. ),

#define F(P)  1./dot(P-U,P-U)     

    F =  F(vec2(0,.3))*J0 + F(M)*J1 + F(vec2(0,-.3))*J2 ;
    O = .5+.5* vec4( dot(F,J0), dot(F,J1),  dot(F,J2), 0);

    O =  max( max( O, F(M)/R.y ),  max( F(vec2(0,.3)) , F(vec2(0,-.3)) ) /R.y );

