#define PI 3.1415926
#define hPI 1.5707963

float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }


float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / min(iResolution.x, iResolution.y);
    //vec2 muv = (iMouse.xy - .5*iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 muv = vec2(sin(iTime / 2.) / 19., sin(iTime / 4.) / 19.);
    //vec2 muv = vec2(0., -0.1);

    float c1 = distance(uv, muv + vec2(0.,    sin(mod(iTime + PI * rand(floor(99. + iTime / 7. )), hPI))) * 1.4) + 0.02;
    float c2 = distance(uv, muv + vec2(-0.07, sin(mod(iTime + PI * rand(floor(33. + iTime / 11.)), hPI))) * 1.4) + 0.02;
    float c3 = distance(uv, muv + vec2(0.06,  sin(mod(iTime + PI * rand(floor(22. + iTime / 13.)), hPI))) * 1.4) + 0.02;
    float c4 = distance(uv, muv + vec2(-0.11, sin(mod(iTime + PI * rand(floor(11. + iTime / 17.)), hPI))) * 1.4) + 0.02;
    float c5 = distance(uv, muv + vec2(0.09,  sin(mod(iTime + PI * rand(floor(66. + iTime / 5. )), hPI))) * 1.4) + 0.02;

    float c0a = atan(uv.y - muv.y, uv.x - muv.x);
    float c0 = (
        (distance(uv, muv) - 0.15)
        + cos(iTime * -2.0 + c0a * 7.0 ) / (40. + abs(sin(iTime * 2.0)) * 80.0)
        + cos(iTime *  1.2 + c0a * 11.0) / (60. + abs(sin(iTime * 3.0)) * 70.0)
        + cos(iTime * -0.4 + c0a * 13.0) / (70. + abs(sin(iTime * 4.0)) * 60.0)
        + cos(iTime *  1.6 + c0a * 17.0) / (90. + abs(sin(iTime * 5.0)) * 50.0)
        + cos(iTime * -0.7 + c0a * 29.0) / (90. + abs(sin(iTime * 7.0)) * 40.0)
        + cos(iTime *  0.2 + c0a * 59.0) / (200. + abs(sin(iTime * 11.0)) * 10.0)
        + cos(iTime * -3.1 + c0a * 113.) / (380. + abs(sin(iTime * 2.0)) * 150.0)
    );

    float a = (
        opSmoothUnion(opSmoothUnion(opSmoothUnion(opSmoothUnion(opSmoothUnion(
            c0, c1, 0.2), c2, 0.2), c3, 0.2), c4, 0.2), c5, 0.2)
    );

    fragColor = vec4(
        smoothstep(0.062, 0., a),
        smoothstep(0.032, 0., a),
        smoothstep(0.002, 0., a),
        1.0
    );
}
