void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    // animate
    float t = iTime/2.0;
	float w = 0.7+0.69*sin(iTime*0.61+0.0);
    float h = 0.4+0.35*sin(iTime*0.53+2.0);
    if( iMouse.x<0.01 ) m=sin(-0.17*iTime*vec2(1.1,1.3)+vec2(0,1));

    // sdf
    float d = sdParabola( p, w, h );


    // colorize
    //vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
    vec3 col = vec3(1.0,0.9,1.0) + vec3(-0.3,0.4,0.3);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.7 + 0.2*cos(160.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    //if( iMouse.z>0.001 )
    {
    d = sdParabola(m, w, h );
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, abs(max(abs(p.x-m.x), abs(p.y-m.y)) - abs(d))));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, length(p-m)-0.015));
    }

    // width and height
    d = length(p-vec2(0.0,h))-0.02;
    d = min(d, length(p-vec2(w,0.0))-0.02);
    col = mix( col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0,0.01,d) );

	fragColor = vec4(col,1.0);
}
