vec3 renderReflect(TObj Obj, vec3 ro, vec3 rd, vec3 col )
{  vec3 p;
   float t;
   vec3 colobj;  vec3 n;

  /////-----------REFLECT--------------
    if (int( Obj.id_color) ==201)
    { vec3 colref;
      for(int i=0; i<2; ++i)
      {  t=RayMarch(ro,rd, MAX_STEPS);
         Obj=mObj;

           if( t<0.0 || t>MAX_DIST ) break;
        p=ro+rd*t;
        n=GetNormal(p);
       rd=reflect(rd,n);
        ro =p+rd * MIN_DIST * 3.;

        if (t<MAX_DIST)
        colobj=GetColorYMaterial( p, n, ro, rd,  int( Obj.id_color), Obj.id_material)*2.0;

        colref=  Getluz( p,ro,rd, n, colobj ,light_pos1);
        colref+= Getluz( p,ro,rd, n, colobj ,light_pos2);

       }
     col+=colref/(float(REFLECT));
   } 
   return col;
} 

vec3 Render(vec3 ro, vec3 rd)
{  vec3 col = vec3(0);
   TObj Obj;
   mObj.rd=rd;
   mObj.ro=ro;
   vec3 p;

   float d=RayMarch(ro,rd, MAX_STEPS);

    Obj=mObj;
    if(mObj.hitbln) 
    {   p = (ro + rd * d );  
        vec3 nor=mObj.normal;
        vec3 colobj;
        colobj=GetColorYMaterial( p, nor, ro, rd,  int( Obj.id_color), Obj.id_material);

        float dif1=1.0;
        vec3 result;
        result=  Getluz( p,ro,rd, nor, colobj ,light_pos1);
        result+= Getluz( p,ro,rd, nor, colobj ,light_pos2);
        col= result;
    }

    col= renderReflect(Obj,  ro,  rd,  col );
   return col;
}

///---------------------------------------------
vec3 linear2srgb(vec3 c) {
    return mix(
        12.92 * c,1.055 * pow(c, vec3(1.0/1.8)) - 0.055,
        step(vec3(0.0031308), c));
}

vec3 exposureToneMapping(float exposure, vec3 hdrColor) 
{    return vec3(1.0) - exp(-hdrColor * exposure);  }

