#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define MX(a,b,c)mix(a,a+b,c)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    float t=iTime*0.8;
    float A=MX(2.5, 0.5, sin(1.5*t + 2.2*sin(0.4*t) + 0.2)*.5+.5);
    float B=MX(2.6, 0.6, sin(2.0*t + 1.5*sin(1.4*t) + 1.4)*.5+.5);
    float D=MX(1.2, 1.0, sin(1.5*t + 0.6*sin(0.8*t) + 0.6)*.5+.5);
    float E=MX(0.1, 0.3, sin(0.6*t + 0.3*sin(0.4*t) + 3.8)*.5+.5);
    float F=MX(7.0, 3.0, sin(1.7*t + 1.5*sin(1.7*t) + 1.0)*.5+.5);
    float G=MX(0.2, 0.3, sin(1.3*t + 1.1*sin(0.4*t) + 1.2)*.5+.5);
    float H=MX(0.6, 0.4, sin(1.2*t + 2.2*sin(1.4*t) + 1.4)*.5+.5);
    vec3 p,q,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<99.;
        e<.001?O.rgb+=mix(vec3(1),H(log(s)*.2),.8)*.015*exp(-5.*i*i*e):r
    )
    {
        p=g*d;
        p.z-=7.;
        p=R(p,normalize(vec3(1,2,3)),iTime*.3);
        q=p*.6;
        s=2.;
        for(int j;j++<8;)
            p=asin(sin(clamp(-p,-A,A)*D)/(1.+E))/D-clamp(p,-B,B)+p,
            e=F*clamp(G/min(dot(p,p),H),0.,1.),
            p=p*e+q,
            s*=e;
        g+=e=length(p)/s-.0005;
    }
}
