float PrRoundBoxDf (vec3 p, vec3 b, float r);
float PrBox2Df (vec2 p, vec2 b);
float PrCapsDf (vec3 p, float r, float h);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float SmoothBump (float lo, float hi, float w, float x);
float Minv3 (vec3 p);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Noisefv2 (vec2 p);

vec3 qHit, pgSize, ltDir, ltDirS;
vec2 hipPos, ftPos;
float tCur, dstFar, dMiss, legLenU, legLenD, aU, aD, pPitch, pRot, szFac;
int idObj;
const int idBase = 1, idLeg = 2, idAxl = 3, idProp = 4;
const float pi = 3.1415927;

#define VAR_ZERO min (iFrame, 0)

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 fb;
  float dMin, d;
  dMin = dstFar / szFac;
  p /= szFac;
  fb = normalize (vec2 (1.2, 1.));
  for (float k = -1. + float (VAR_ZERO); k <= 1.; k += 2.) {
    q = p;
    q.xz = Rot2D (q.xz, k * pRot);
    q.y -= hipPos.y + 2.4 + 1.2 * k;
    d = PrRoundCylDf (q.xzy, 0.2 - 0.05 * k, 0.01, 1.2);
    DMINQ (idAxl);
    q.y -= 1.;
    d = min (max (PrRoundCylDf (vec3 (q.x, Rot2D (q.yz, k * pPitch)).xzy, 3., 0.02, 0.01),
       dot (vec2 (q.x, abs (q.z)), fb)),
       max (PrRoundCylDf (vec3 (q.x, Rot2D (q.yz, - k * pPitch)).xzy, 3., 0.02, 0.01),
       dot (vec2 (- q.x, abs (q.z)), fb)));
    DMINQ (idProp);
  }
  q = p;
  q.y -= hipPos.y + 0.1;
  d = PrRoundCylDf (q.xzy, hipPos.x, 0.05, 0.2);
  q.y -= 0.4;
  d = min (d, PrRoundCylDf (q.xzy, 0.35 * hipPos.x, 0.05, 0.15));
  DMINQ (idBase);
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * atan (q.z, - q.x) / (2. * pi)) + 0.5) / 6.);
  q.xy -= hipPos * vec2 (-1., 1.);
  q.xy = Rot2D (q.xy, aU);
  q.x += 0.5 * legLenU;
  d = PrCapsDf (q.yzx, 0.1 * (0.8 + 0.2 * q.x / (0.5 * legLenU)) / 0.6, 0.5 * legLenU);
  DMINQ (idLeg);
  q.x += 0.5 * legLenU;
  q.xy = Rot2D (q.xy, aD - aU);
  q.x += 0.5 * legLenD;
  d = PrCapsDf (q.yzx, 0.1 * (0.9 + 0.1 * q.x / (0.5 * legLenD)), 0.5 * legLenD);
  DMINQ (idLeg);
  q.x += 0.5 * legLenD;
  q.xy = Rot2D (q.xy, 0.5 * pi - aD);
  q.x -= -0.02;
  d = PrRoundCylDf (q.yzx, 0.3, 0.02, 0.05);
  DMINQ (idLeg);
  return szFac * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 ObjNfL (vec3 p)
{
  vec4 v;
  vec2 e;
  float s;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  s = dot (v, vec4 (1.));
  return vec4 (normalize (2. * v.yzw - s), (s - 2. * v.x - 4. * ObjDf (p)) / (e.x * e.x));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

float ExObjDf (vec3 p)
{
  vec3 q;
  float dMin, d;
  dMin = dstFar;
  q = p;
  q -= vec3 (8., 0.4, 6.);
  q.xz = Rot2D (q.xz, 0.2 * pi);
  d = PrRoundCylDf (q, 0.33, 0.02, 5.);
  d = max (d, - dot (vec2 (q.z, dot (q.xy, cos (2. * pi * floor (16. * (atan (q.y, - q.x) / (2. * pi) +
     0.5) + 0.5) / 16. + vec2 (0., 0.5 * pi)))), sin (-0.4 * pi + vec2 (0.5 * pi, 0.))) - 1.6);
  DMINQ (1);
  q = p;
  q.yz -= vec2 (-0.1, 4.);
  d = max (PrRoundBoxDf (q, vec3 (pgSize + vec3 (1., 0.1, 1.)), 0.03), - PrBox2Df (q.xz, pgSize.xz));
  DMINQ (2);
  return dMin;
}

float ExObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d = ExObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ExObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ExObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ExObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.01;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ExObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 ShGrid (vec2 p, vec2 g, int stag)
{
  vec2 q, sq, ss;
  q = p * g;
  if (stag > 0 && 2. * floor (0.5 * floor (q.y)) != floor (q.y)) q.x += 0.5;
  sq = smoothstep (0.05, 0.1, abs (fract (q + 0.5) - 0.5));
  q = fract (q) - 0.5;
  ss = 0.3 * smoothstep (0.3, 0.5, abs (q.xy)) * sign (q.xy);
  if (abs (q.x) < abs (q.y)) ss.x = 0.;
  else ss.y = 0.;
  return vec3 (ss.xy, 1. - 0.1 * sq.x * sq.y).xzy;
}

vec2 ExBlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, fcBlk;
  vec2 qBlk;
  float dn, df, bSize;
  bSize = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, fcBlk), dot (u.yzx, fcBlk)) / bSize;
  } else qBlk = vec2 (0.);
  return qBlk;
}

vec4 BlkHit (vec3 ro, vec3 rd, vec3 bSize)
{
  vec3 v, tm, tp, fcBlk;
  vec2 qBlk;
  float dMin, dn, df;
  dMin = dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = Maxv3 (tm);
  df = Minv3 (tp);
  if (df > 0. && dn < df) {
    dMin = dn;
    fcBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  }
  return vec4 (dMin, fcBlk);
}

vec4 ObjCol ()
{
  vec4 col4;
  if (idObj == idBase) {
    col4 = vec4 (0.9, 0.9, 1., 0.2) * (0.8 + 0.2 * smoothstep (0., 0.02,
       abs (length (qHit.xz) - 1.1) - 0.1));
  } else if (idObj == idLeg) {
    col4 = vec4 (0.9, 0.9, 1., 0.2) * (0.8 + 0.2 * sin (8. * pi * qHit.x));
  } else if (idObj == idAxl) {
    col4 = vec4 (0.8, 1., 0.8, 0.2) * (1. - 0.2 * sin (4. * atan (qHit.z, qHit.x)));
  } else if (idObj == idProp) {
    col4 = vec4 (1., 0.9, 0.8, 0.2) * (0.9 + 0.1 * sin (8. * pi * length (qHit.xz)));
  }
  return col4;
}

#define ACOS_CLAMP(c) acos (clamp ((c), -1., 1.))

void SetConf (float vp)
{
  vec2 knPos, r, a, b;
  float tCyc, rl, s, t;
  legLenU = 2.;
  legLenD = 3.;
  tCyc = 6.;
  t = mod (tCur / tCyc, 1.);
  pPitch = 0.05 * pi * (0.5 + 1.5 * SmoothBump (0.05, 0.5, 0.03, t));
  hipPos = vec2 (1.5, 0.5 * (legLenU + legLenD) + 6. * step (vp, -0.5) * SmoothBump (0.25, 0.75, 0.2, t));
  ftPos = vec2 (1.8 + 2.2 * (1. - smoothstep (0.2, 1.5, hipPos.y - (legLenU + legLenD))), 0.);
  r = ftPos - hipPos;
  rl = length (r);
  b = vec2 (legLenU, legLenD);
  if (rl < legLenU + legLenD) {
    a = ACOS_CLAMP ((rl * rl + (b.y * b.y - b.x * b.x) * vec2 (-1., 1.)) / (2. * rl * b));
    s = sign (r.x);
    knPos = vec2 (hipPos.x, ftPos.y) + b * sin (vec2 ((a.x + 0.5 * pi) * s, a.y) -
       asin (r.y / rl) * vec2 (- s, 1.));
    r = knPos - hipPos;
    aU = atan (r.y, r.x);
    r = ftPos - knPos;
    aD = atan (r.y, r.x);
  } else {
    aU = asin (r.x / (legLenU + legLenD)) - 0.5 * pi;
    aD = aU;
  }
  pRot = ((vp != 1.) ? 2. : 0.2) * pi * tCur;
}

vec3 SkShowScene (vec3 ro, vec3 rd, vec2 uv)
{
  vec4 col4, vn4;
  vec3 col, vn;
  float dstObj;
  dMiss = dstFar;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn4 = ObjNfL (ro);
    vn = vn4.xyz;
    col4 = ObjCol ();
    col = vec3 (1.) * Maxv3 (col4.rgb);
    col *= (1. - 0.5 * smoothstep (0.5, 1., abs (vn4.w) / 64.)) *
       (0.9 + 0.2 * Noisefv2 (Rot2D (uv, -0.2 * pi) * vec2 (16., 256.))) *
       (0.5 + 0.5 * max (dot (vn, ltDirS), 0.));
    dMiss = dstFar;
  } else {
    col = vec3 (1.);
  }
  col = mix (col, vec3 (0.3), exp (- 64. * dMiss));
  return clamp (col, 0., 1.);
}

vec3 SkMain (vec2 fCoord, float vp)
{
  mat3 vuMat;
  vec3 ro, rd, col;
  vec2 uv;
  float el, az, szFacT;
  szFacT = szFac;
  uv = fCoord / vec2 (5., 5.5);
  if (vp == 0.) {
    szFac = 0.7;
    uv -= vec2 (-0.35, -1.7);
    el = -0.05 * pi;
    az = 0.;
  } else if (vp == 1.) {
    szFac = 0.65;
    uv -= vec2 (0.3, -1.2);
    el = -0.5 * pi;
    az = 0.17 * pi;
  } else if (vp == 2.) {
    szFac = 0.63;
    uv -= vec2 (0., -1.6);
    el = -0.25 * pi;
    az = 0.7 * pi;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., -5., -20.);
  rd = vuMat * normalize (vec3 (uv, 4.2));
  ltDirS = vuMat * normalize (vec3 (0.7, 1., -0.7));
  SetConf (vp);
  col = SkShowScene (ro, rd, uv);
  szFac = szFacT;
  return clamp (col, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, db4;
  vec3 col, vn, qHitEx;
  vec2 qBlk, q, sq;
  float dstObj, dstExObj, sh, r;
  int idObjEx;
  bool isBg;
  SetConf (-1.);
  szFac = 0.7;
  pgSize = vec3 (15., 0.1, 10.);
  isBg = false;
  dstExObj = ExObjRay (ro, rd);
  idObjEx = idObj;
  qHitEx = qHit;
  dstObj = ObjRay (ro, rd);
  db4 = BlkHit (ro - vec3 (0., -0.1, 4.), rd, pgSize);
  if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
    if (dstObj < dstExObj) {
      ro += dstObj * rd;
      vn = ObjNf (ro);
      qBlk = mod (2. * ExBlkHit (ro, reflect (rd, vn)), 1.);
      col4 = ObjCol ();
    } else {
      ro += dstExObj * rd;
      vn = ExObjNf (ro);
      if (idObjEx == 1) {
        r = length (qHit.xy);
        col4 = mix (vec4 (0.9, 0.7, 0.1, 0.), vec4 (1., 0.9, 0.1, 0.01), step (0.34, r));
        col4 = mix (vec4 (0.3, 0.3, 0.3, 0.), col4, step (0.18, r));
        col4 = mix (vec4 (0.8, 0.4, 0.1, 0.2), col4, step (qHitEx.z, 4.5));
      } else if (idObjEx == 2) {
        col4 = vec4 (0.5, 0.2, 0.1, 0.) * (0.9 + 0.1 * Noisefv2 (32. * qHit.xz));
      }
    }
  } else if (db4.x < dstFar) {
    ro += db4.x * rd;
    vn = db4.yzw;
    q = ro.xz - vec2 (0., 4.);
    if (vn.y > 0.5 && PrBox2Df (q, pgSize.xz - 0.96) < 0.) {
      sq = sign (q);
      col = (sq.x < 0. || sq.y < 0.) ? SkMain (q - 0.5 * sq * pgSize.xz,
         sq.y + 1. + 0.5 * (sq.x + 1.)) : vec3 (1.);
      if (Minv3 (col) > 0.95) {
        q = smoothstep (0.02, 0.05, abs (mod (q + 0.5, 1.) - 0.5));
        col = mix (vec3 (0.4, 0.6, 0.9), vec3 (1.), min (q.x, q.y));
      }
    } else {
      col = vec3 (1.) * (0.8 + 0.2 * step (0.5, vn.y));
      sh = 1.;
    }
  } else {
    isBg = true;
  }
  if (! isBg) {
    SetConf (-1.);
    sh = min (ObjSShadow (ro + 0.01 * vn, ltDir), ExObjSShadow (ro + 0.01 * vn, ltDir));
    if (min (dstObj, dstExObj) < min (db4.x, dstFar)) {
      col = col4.rgb * (0.2 + 0.8 * sh * max (dot (vn, ltDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
      col += col4.a * vec3 (0.5) * (0.5 + 0.5 * SmoothBump (0.25, 0.75, 0.05, qBlk.x) *
         SmoothBump (0.25, 0.75, 0.05, qBlk.y));
    } else {
      col *= 0.3 + 0.7 * sh * max (dot (vn, ltDir), 0.);
    }
  } else {
    qBlk = mod (8. * ExBlkHit (ro, rd), 1.);
    col = vec3 (0.5, 0.4, 0.4) * (0.05 + 0.245 * (rd.y + 1.) * (rd.y + 1.)) +
       vec3 (0.2) * (0.9 + 0.1 * SmoothBump (0.25, 0.75, 0.1, qBlk.x) *
       SmoothBump (0.25, 0.75, 0.1, qBlk.y));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.27 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.8 * pi * mPtr.y;
  }
  el = clamp (el, -0.5 * pi, -0.1 * pi);
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 1.5, -40.);
  rd = vuMat * normalize (vec3 (uv, 4.7));
  dstFar = 100.;
  ltDir = vuMat * normalize (vec3 (0.7, 1., -0.7));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}
