vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf


    res =opU3(res, vec3(planeDist1,100.0,-1.0)); //inf

    p.y=p.y-6.5;

    p.z=opRep1D(p.z, 15.0 );

    float r1=7.0;
    float r2=3.0;
    float he=7.0;

    float sdt1= sdTrapezoid(p.xy, r1,r2,he )   ;
     r1=6.0;
     r2=2.5;
     he=6.5;

    float sdt2= sdTrapezoid(p.xy, r1,r2,he );

    float sdb1= sdBox(p, vec3(10.0,10.0,3.0) );
    float sdb2= sdBox(p, vec3(10.0,10.0,3.5) );

    float sinter1= intersectSDF(sdt1,sdb1);
    float sinter2= intersectSDF(sdt2,sdb2);
    float sdif= differenceSDF(sinter1,sinter2);
    res =opU3(res, vec3(sdif,104.0,MATERIAL_NO)); 

    //res =opU3(res, vec3(1.0,0.0,MATERIAL_NO)); 
    //return (dist, id_color, id_material)
    return res;
}

//------------------------------------------------
vec3 GetNormal(vec3 p)
{   float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);
    vec3 n = d - vec3(GetDist(p-e.xyy).x,GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);
    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, int PMaxSteps)
{   float t = 0.; 
    vec3 dS=vec3(9999.0,-1.0,-1.0);
    float marchCount = 0.0;
    vec3 p;
    float minDist = 9999.0; 

    for(int i=0; i <= PMaxSteps; i++) 
    {  	p = ro + rd*t;
        dS = GetDist(p);
        t += dS.x;
        if ( abs(dS.x)<MIN_DIST  || i == PMaxSteps)
            {mObj.hitbln = true; minDist = abs(t); break;}
        if(t>MAX_DIST)
            {mObj.hitbln = false;    minDist = t;    break; } 
        marchCount++;
    }
    mObj.dist = minDist;
    mObj.id_color = dS.y;
    mObj.marchCount=marchCount;
    mObj.id_material=dS.z;
    mObj.normal=GetNormal(p);
    mObj.phit=p;
    return t;
}

