// === particle Physics: caustic surface = surface mesh BuffD offseted in the refracted direction.

void mainImage( out vec4 O, vec2 U )
{

#define T(U) texture( iChannel2, (U)/R ).r

    vec3 L = vec3(0,0,-1);
    vec2 d = vec2(1,0);
    float M = iMouse.z > 0. ? 2.* iMouse.y/R.y - 1.
                            : sin(iTime);

    O = R.y*vec4( U/R.y, T(U), 1 );      // O.xyz = 3D height field
    vec3 N = normalize(vec3( T(U+d)-T(U-d), T(U+d.yx)-T(U-d.yx), 2./R.y ) ),
         D = refract(L,N, 1./1.33);      // refracted light direction
    O.xyz += R.y* .15 * M * D;           // offset the lightwave surface
}

// === Vorono√Ø buffer: manage tracking of particles Ids

// --- insert (i,d) and maintain the 4 closest (i_,d_) 
void list_insert(inout vec4 i, inout vec4 d, float i_, float d_){	
    if(i_ == 0.) return;           // not a particle : exit
    if(any(equal(vec4(i_),i))) return; // already in top4 : exit
    if     (d_ < d[0])             // closer to closest: insert here
        i = vec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             // closer to 2nd closest: insert here
        i = vec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])             // closer to 3rd closest: insert here
        i = vec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])             // closer to 4th closest: insert here
        i = vec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

void mainImage( out vec4 O, vec2 I )
{
    vec4  i = vec4(0),
         i0 = T1( I ),             // 4 closests particles here and around
         ia = T1( I + vec2( 1, 0) ),  // NB: could use an array.
         ib = T1( I + vec2( 0, 1) ),
         ic = T1( I + vec2(-1, 0) ),
         id = T1( I + vec2( 0,-1) );

//#define dist(i) length( A(i).xy - I )
//#define dist(i) length( mod( A(i).xy-I + R/2., R) - R/2. )  
vec2 D;
#define dist(i) ( D = mod( A(i).xy-I + R/2., R) - R/2., dot(D,D) )

    vec4  d = vec4(1e9); 
    for(int k = 0; k < 4; k++){    // sorts all these
        list_insert( i, d, i0[k], dist(i0[k]) );
        list_insert( i, d, ia[k], dist(ia[k]) );
        list_insert( i, d, ib[k], dist(ib[k]) );
        list_insert( i, d, ic[k], dist(ic[k]) );
        list_insert( i, d, id[k], dist(id[k]) );
    }
#if 0 // also checking diagonal (to test possibly axis bias)
    ia = T1( I + vec2( 1, 1) ),
    ib = T1( I + vec2(-1, 1) ),
    ic = T1( I + vec2( 1,-1) ),
    id = T1( I + vec2(-1,-1) );
    for(int k = 0; k < 4; k++){    // sorts all these
        list_insert( i, d, ia[k], dist(ia[k]) );
        list_insert( i, d, ib[k], dist(ib[k]) );
        list_insert( i, d, ic[k], dist(ic[k]) );
        list_insert( i, d, id[k], dist(id[k]) );
    }
#endif

    for(int k = 0; k < 1; k++){    // try to re-insert some random particle (possibly escaped from tracking)
      //int r = IHash( int(I.x) + int(I.y)*2048 + iFrame*2048*2048 +k*11131); //deterministic
        int r = IHash( int(I.x) + int(I.y)*2141 + iFrame*2141*2141 +k*11131); //without 2048 bias
      //int r = IHash( int(I.x) + int(I.y)*2048 + (int(iTime*2048.)+iFrame)*2048 +k*11131);
        int i_ =  1 + r % ( int(R.x*R.y)/int(Nb) ); // [ why /10? ]
        list_insert(i, d, float(i_), dist(i_) );
    }
    O = vec4(i);                   // stores 4 closest

}

#define Nb 1. // 10. use only 1/N % of the max Rx*Ry particles (for readability)

// Buff B (1) store Vorono√Ø tracking acceleration structure;
//            xyzw: ids of 4 closest partics to buffer location
// Buff A (0) stores particles: 
//            xy: position zw: velocity

// --- translate particle id (in [1,Rx*Ry] ) to buffer pixel 
#define A(n) T0( vec2( (int(n)-1) % iR.x,      \
                       (int(n)-1) / iR.x ) +.5 )  // +.5 useless

// --- utils

#define R     iResolution.xy
#define iR    ivec2(iResolution)
#define T0(U) texelFetch( iChannel0, ivec2(U)   , 0 )
#define T1(U) texelFetch( iChannel1, ivec2(U)%iR, 0 )
#define T2(U) texelFetch( iChannel2, ivec2(U)   , 0 )

#define l2(x) dot(x,x)

#define TAU 6.2831853

#define hue(v)  ( .6 + .6 * cos( 6.3*(v)  + vec4(0,23,21,0)  ) )

// --- keyboard
#define key(k,mode) ( texelFetch( iChannel3, ivec2(k,mode), 0 ).x > .5 )
#define keyDown(k) key(k,0)
#define  keyHit(k) key(k,1)
#define keyFlip(k) key(k,2)


// --- random numbers

int IHash(int a){
	a = (a ^ 61) ^ (a >> 16);
	a = a + (a << 3);
	a = a ^ (a >> 4);
	a = a * 0x27d4eb2d;
	a = a ^ (a >> 15);
	return a;
}

#define Hash(a) ( float(IHash(a)) / float(0x7FFFFFFF) ) // Uniform in [0,1]

vec4 rand4(int seed){
    return vec4(Hash(seed^0x34F85A93),
                Hash(seed^0x85FB93D5),
                Hash(seed^0x6253DF84),
                Hash(seed^0x25FC3625));
}

// --- normal law random generator
vec2 randn(vec2 r){ // r: randuniform
    r.x = sqrt( -2.* log(1e-9+abs(r.x)));
    r.y *= TAU;
    return r.x * vec2(cos(r.y),sin(r.y));
}

// === draw + blend with fading past

//  WIP: for doing lightwave surface
float line(vec2 p, vec2 a,vec2 b) { 
    p -= a, b -= a;
    float h = clamp(dot(p, b) / dot(b, b), 0., 1.);   // proj coord on line
    return length(p - b * h);                         // dist to segment
}
#define Ad(n,X,Y) T0( vec2( (int(n)-1) % iR.x,      \
                       (int(n)-1) / iR.x ) +.5 +vec2(X,Y) )  // +.5 useless

void mainImage( out vec4 O, vec2 I )
{
    O = vec4(0);
    vec4 a = T1(I), P;         // 4 particule id (supposed to be particles closest to I)

    float d = 99.;
    for(int i = 0; i < 4; i++) // draw Gaussian blobs
        P = A(a[i]),
 #if 0                         // WIP: drawing mesh of lightwave surface
        d = min(d, line(I,P.xy, Ad(a[i],-1, 0).xy) ),
        d = min(d, line(I,P.xy, Ad(a[i], 0, 1).xy) ),
        d = min(d, line(I,P.xy, Ad(a[i], 1, 0).xy) ),
        d = min(d, line(I,P.xy, Ad(a[i], 0,-1).xy) );
    O = vec4(smoothstep(1.5,0.,d));
#else
        O += .4* exp( -.5* l2( I - P.xy ) ) 
             ; //*  ( .5+.5*texture(iChannel3,P.xy/R) ); // color surface by the source texture
    O = mix(O, T2(I), .9);     // blend with fading past
 // O = mix(O, texture(iChannel2,-.01+1.02*I/R), .9); // false-3D variant
#endif
}

// --- source height field

void mainImage( out vec4 O, vec2 U )
{
    O = texture(iChannel0,.3*U/R) / .3;
 // U *= 30./R.y; O = vec4( sin(U.x + sin(.3*U.y) - 0.*iTime ) );
}
