        float circle(vec2 uv, vec2 center, float radius)
        {
        float edge = 0.008;
        float up = center.y + sqrt(pow(radius, 2.0) - pow(uv.x - center.x, 2.0));
        float down = center.y - sqrt(pow(radius, 2.0) - pow(uv.x - center.x, 2.0));
        return (1.0 - smoothstep(up-edge, up, uv.y)) *
        smoothstep(down, down + edge, uv.y);
        }
   void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
        vec2 st = fragCoord/iResolution.xy;
        float m_time = 0.5 + 0.5*sin(iTime * 0.5); 

        float skyline = 0.276;  

        // color
        vec4 bg_col = vec4(1, 1, 1, 1);
        vec4 color = bg_col;

        // sun
        vec2 s_center = vec2(0.5 + 0.15 * m_time,0.984-0.68*m_time);
        float cir = circle(st, s_center, 0.080);
        vec4 sun_col = mix(vec4(1.000,0.916,0.717,1.000),vec4(1.000,0.089,0.136,1.000), m_time);

        // sky
        float skyedge = 0.042;
        float sky = smoothstep(skyline,skyline + skyedge,st.y);
        vec4 skycolor_up = mix(vec4(0.376,0.891,0.985,1.000),vec4(0.293,0.393,0.710,1.000), m_time);
        vec4 skycolor_down = mix(vec4(0.843,0.931,1.000,1.000),vec4(1.000,0.461,0.148,1.000), m_time);
		   vec4 sky_col=mix(skycolor_down, skycolor_up, (st.y-skyline) * 1.0/(1.0-skyline));
        // sea
        float sea = 1.0 - sky;
        vec4 sea_col =mix( mix(vec4(0.096,0.405,0.985,1.000),vec4(0.092,0.277,0.750,1.000), m_time),
        mix(vec4(0.216,0.985,0.966,1.000),vec4(0.517,0.830,0.045,1.000), m_time),(st.y-skyline) * 1.0/(skyline));

        // reflection
        vec4 ref_col;
        vec4 ref_col1=mix(sun_col,sea_col,st.x);
        vec4 ref_col2=mix(sun_col,sea_col,st.y);
        ref_col=mix(ref_col1,ref_col2,m_time);
        float ref_width = 0.356 - -0.054 * m_time;
        float ref = sea * (smoothstep(
        s_center.x+ 0.820 * st.y - ref_width,
        s_center.x, st.x) -
        smoothstep(s_center.x,
        s_center.x-0.932*st.y + ref_width, st.x));

        // color
        color = (1.0 - sky)*color + sky *sky_col; 
        color = (1.0 - cir)*color +cir * sun_col; 
        color = (1.0 - sea)*color + sea * sea_col;
    	color = (1.0 - ref)*color + ref * ref_col; 

        fragColor = color;
        }
