// You can use this shader as a template for ray marching shaders

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}



float rand(vec2 co){return fract(sin(dot(co.xy,vec2(12.9898,78.233)))*43758.5453);}



float sdfPlanet(vec3 p,float r){
    return length(p)-r;
}

float map(vec3 p){
    mat2 rot=Rot(iTime*.08);

    vec3 bp=p;
    bp.xz*=rot;

    float d=sdfPlanet(bp,.4);
    d+=texture(iChannel2,bp.xy*3.).r*.015;
    return d;
}


float GetDist(vec3 p) {
    float d = sdBox(p, vec3(1));
    d=map(p);//length(p)-10.5;
    /*vec2 uv = vec2(atan(p.x, p.z)/6.2832, p.y/3.)+.5;
    float disp = texture(iChannel0, uv).r;

    disp *= smoothstep(1.4, 1., abs(p.y));
    d -= disp*.3;*/

    return d=map(p);
}

vec3 Transform(vec3 p) {
     /*p.xy *= Rot(iTime*.4);
     p.xz *= Rot(iTime*.2);*/
     p.xz*=Rot(T*.2);
     p.xy*=Rot(T*.1);
     p.yz*=Rot(T*.15);

     return p;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = Transform(ro + rd*dO);    	
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

float stars(vec2 uv,float time){
    vec2 fuv=fract(uv*20.);
    vec2 fluv=floor(uv*40.);
    float res;
    for(int y=-1;y<=1;y++){
        for(int x=-1;x<=1;x++){
            vec2 offs=vec2(x,y);
            float r=rand(fluv+offs);
            float l=length(fuv-offs-r);
            float d=(.02*r*r/l);
            d=smoothstep(.01,1.,d);
            res+=d*((1.-sin(r*r*time*2.)));
            res-=max(uv.y-uv.x-.5,0.);
        }
    }
    return clamp(res,.01,1.);
}

vec3 lights(vec3 n, vec2 fragCoord, vec2 uv, vec3 col)
{
    mat2 rot=Rot(iTime*.08);
    // Lighting
    n.xz*=inverse(rot);

    vec3 light=vec3(.5);
    vec3 sun_lig=normalize(vec3(-.5,.45,-.3));
    float sun_dif=clamp(dot(n,sun_lig),0.,1.);
    light+=sun_dif*vec3(.6,.6,.6);

    vec3 sky_lig=normalize(vec3(0.,.5*n.y+.5,0.));
    float sky_dif=clamp(dot(n,sky_lig),0.,1.);
    light+=sky_dif*vec3(.3,.05,.01);

    vec3 planet_lig=normalize(vec3(-.05,.03,.4));
    float planet_dif=smoothstep(.9,1.,clamp(dot(n,planet_lig),0.,1.))*4.;
    light+=planet_dif*vec3(.2,1.,1.);//+n*-0.1;

    col*=light;
    vec2 pv=fragCoord/iResolution.xy;
    vec3 curve=texture(iChannel1,pv).rgb;
    col+=curve;

    uv-=vec2(-.58,.35);
    vec2 bfuv=abs(uv*mat2(2.5,0,1.4,1.7))*mat2(2,sin(iTime)*.2-2.,sin(iTime)*.4-.5,1.5);
    float bright_flare=.1/max(bfuv.x,bfuv.y)*smoothstep(-.7,1.,uv.x);

    col+=bright_flare*1.*vec3(.8,.9,.6);
    return col;
    }

    // The constant Pi.
float pi = 3.14159;

// Sphere radius, relative.
float radius_rel = 0.45;

// Sphere centre, relative.
vec2 centre_rel = vec2(0.5, 0.5);

// Sphere rotation speed, rpm.
float rot_vel = 5.0;

vec4 circle(out vec4 color, in vec2 coord)
{
    // Sphere radius, px.
    float radius = radius_rel * min(iResolution.x, iResolution.y);

    // Sphere centre, px.
    vec2 centre = centre_rel * iResolution.xy;

    // Screen coordinates relative to sphere centre.
    vec2 coord_rel = coord - centre;

    // Relative screen coordinates normalised to [-1, 1].
    vec2 coord_rel_norm = coord_rel / radius;

    // Relative and normalised screen coordinates squared.
    vec2 coord_rel_norm_sq = coord_rel_norm * coord_rel_norm;

    // Output black if screen coordinates outside sphere.
    if (coord_rel_norm_sq.x + coord_rel_norm_sq.y > 1.0)
    {    
		color = vec4(0.0, 0.0, 0.0, 1.0);
    	return color;
    }

    // Ratio between parallel radius and sphere radius.
    float radius_ratio = cos(asin(coord_rel_norm.y));

    // Texture coordinates for these screen coordinates.
    vec2 coord_tex = 0.5 + vec2(
        asin(coord_rel_norm.x / radius_ratio),
        asin(coord_rel_norm.y)) / pi;

    // Rotate sphere according to elapsed time.
    //coord_tex.x -= rot_vel * iTime / 30.0;

	// Output color at these texture coordinates.
    color = texture(iChannel0, coord_tex);
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0, -50.+fract(mix(10.,10.1, T))*100.);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    //ro= Transform(ro);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 1.);
    uv = fragCoord/iResolution.xy;//
    vec3 col = texture(iChannel2, uv).xyz;//vec3(0);
    {
    uv = fragCoord/iResolution.xy;
    vec3 col1 = texture(iChannel0, uv).xyz;
    vec3 col2 = texture(iChannel1, uv).xyz;
    }
    float d = RayMarch(ro, rd);
    vec3 p = Transform(ro + rd * d);
    vec3 n = GetNormal(p);
    if(d<MAX_DIST) {


        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        //6 faces texturing
        vec3 colXZ = circle(  fragColor,  fragCoord ).rgb;//texture(iChannel0, p.xz*.5+.5).rgb;
        vec3 colYZ = circle(  fragColor,  fragCoord ).rgb;//texture(iChannel0, p.yz*.5+.5).rgb;
        vec3 colXY = circle(  fragColor,  fragCoord ).rgb;//texture(iChannel0, p.xy*.5+.5).rgb;

        n = abs(n);


        n *= pow(n, vec3(2));
        n /= n.x+n.y+n.z;

        col = colYZ*n.x + colXZ*n.y + colXY*n.z;
    }else{
        uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
        col+=stars(uv,iTime)*1.;
    }

    col = lights(n, fragCoord, uv, col);


    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

float det=.001,t, boxhit;
vec3 adv, boxp;

float hash(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


mat2 rot(float a)
{
    float s=sin(a), c=cos(a);
    return mat2(c,s,-s,c);
}

vec3 path(float t)
    {
    vec3 p=vec3(vec2(sin(t*.1),cos(t*.05))*10.,t);
    p.x+=smoothstep(.0,.5,abs(.5-fract(t*.02)))*10.;
    return p;
}

float fractal(vec2 p)
{
    p=abs(5.-mod(p*.2,10.))-5.;
    float ot=1000.;
    for (int i=0; i<7; i++)
    {
        p=abs(p)/clamp(p.x*p.y,.25,2.)-1.;
        if(i>0)ot=min(ot,abs(p.x)+.7*fract(abs(p.y)*.05+t*.05+float(i)*.3));

    }
    ot=exp(-10.*ot);
    return ot;
}

float box(vec3 p, vec3 l)
{
    vec3 c=abs(p)-l;
    return length(max(vec3(0.),c))+min(0.,max(c.x,max(c.y,c.z)));
}

float de(vec3 p)
{
    boxhit=0.;
    vec3 p2=p-adv;
    p2.xz*=rot(t*.2);
    p2.xy*=rot(t*.1);
    p2.yz*=rot(t*.15);
    float b=box(p2,vec3(1.));
    p.xy-=path(p.z).xy;
    float s=sign(p.y);
    p.y=-abs(p.y)-3.;
    p.z=mod(p.z,20.)-10.;
    for (int i=0; i<5; i++)
    {
        p=abs(p)-1.;
        p.xz*=rot(radians(s*-45.));
        p.yz*=rot(radians(90.));
    }
    float f=-box(p,vec3(5.,5.,10.));
    float d=min(f,b);
    if (d==b) boxp=p2, boxhit=1.;
    return d*.7;
}


vec3 march(vec3 from, vec3 dir)
{
    vec3 p,n,g=vec3(0.);
    float d, td=0.;
    for (int i=0; i<80; i++)
    {
        p=from+td*dir;
        d=de(p)*(1.-hash(gl_FragCoord.xy+t)*.3);
        if (d<det && boxhit<.5) break;
        td+=max(det,abs(d));
        float f=fractal(p.xy)+fractal(p.xz)+fractal(p.yz);
        //boxp*=.5;
        float b=fractal(boxp.xy)+fractal(boxp.xz)+fractal(boxp.yz);
        vec3 colf=vec3(f*f,f,f*f*f);
        vec3 colb=vec3(b+.1,b*b+.05,0.);
        g+=colf/(3.+d*d*2.)*exp(-.0015*td*td)*step(5.,td)/2.*(1.-boxhit);
        g+=colb/(10.+d*d*20.)*boxhit*.5;
    }
    return g;
}

mat3 lookat(vec3 dir, vec3 up) 
{
	dir=normalize(dir);vec3 rt=normalize(cross(dir,normalize(up)));
    return mat3(rt,cross(rt,dir),dir);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    t=iTime*7.;
    vec3 from=path(t);
    adv=path(t+6.+sin(t*.1)*3.);
    vec3 dir=normalize(vec3(uv,.7));
    dir=lookat(adv-from,vec3(0.,1.,0.))*dir;
    vec3 col=march(from, dir);
    fragColor=vec4(col,1.0);
}

/*

  Video Transitions by Mark Craig (Copyright Â© 2021)

    I originally wrote this as a "filter" to create transition frames
  for videos (C program which outputted PPM files).  But eventually,
  I figured it could be relatively easily ported to GLSL - this is the
  result.

*/

int n = 50;            // number of "frames" in transition
float sa = .05;        // smooth amount - size of smooth edges
bool roto = true;      // if true, rotate transitions that can optionally rotate
float rota = 360.0;    // amount of rotation from start to end of transition
int rn = 1, rd = 0;    // select based on whether to start with full
//int rn = 0, rd = -1; //   frame of source1 (only for some transitions)

#define SPEEDADJ .75
//#define NEEDMOD 1

#define imod(a,b) (int((float(a)-(float(b)*floor(float(a)/float(b))))))

#define iGlobalTime (iTime / SPEEDADJ)

#define num_transitions 33

// macros for selecting/mixing the textures

#define MEM2 col = alt ? col1 : col2;
#define MEM1 col = alt ? col2 : col1;
#define MEM12 col = alt ? v1 * col2 + v2 * col1 : v1 * col1 + v2 * col2;
#define MEM1S { col = alt ? texture2D(iChannel1, uv2).xyz : texture2D(iChannel0, uv2).xyz; }

// Solve some incompatibilities

#define atan2 atan
#define fmod mod
#define texture2D texture

// Useful values

#define _TWOPI 6.283185307
#define M_PI 3.141592654

float intersect(vec2, vec2, vec2, vec2);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv = fragCoord.xy / iResolution.xy, uv2;
bool alt = false;
int type, i, i2, j;
vec3 col, col3;
float v1, v2;
float ye, yfe, dx, dy, cx, cy, rad, slope, theta, xc1, yc1, b, rad0, rad2;
float xc2, yc2, b2, cx2, cy2, r1, vy1, vy2, dx2, dy2, ro, ri, ang, a, a1;
float r, r2, l, l2, theta2, theta3, tang, ang1, ang2, angt, angs, c1, s1;
float xc0, yc0;
vec2 p1, p2, po, pd;

float aspect = iResolution.y / iResolution.x;
vec3 col1 = texture2D(iChannel0, uv).xyz;
vec3 col2 = texture2D(iChannel1, uv).xyz;
#ifdef NEEDMOD
i = imod(int(iGlobalTime * 20.0) , n);
type = imod((int(iGlobalTime * 20.0) / n) , num_transitions);
if (imod(type , 2)== 1) { alt = true; } else { alt = false; }
#else
i = int(iGlobalTime * 20.0) % n;
type = (int(iGlobalTime * 20.0) / n) % num_transitions;
if (type % 2 == 1) { alt = true; } else { alt = false; }
#endif

switch (type)
				{
				case 1:
					col1 = texture2D(iChannel2, uv).xyz;
                    col2 = texture2D(iChannel3, uv).xyz;
					break;
                default:
					col1 = texture2D(iChannel0, uv).xyz;
                    col2 = texture2D(iChannel1, uv).xyz;
					break;
				}
//type = 32;

// some of these equations are long, so I'll forego extra spaces

// was a switch, but some GPUs won't do switch

		if (type==0) // fade in/out
			{
			v1=float(n-(i+1))/float(n-1); v2=1.0-v1;
			MEM12
			}
		else if (type==1) // window down with soft edge
			{
			ye=1.0-float(i)/float(n-1);
			yfe=ye+sa;
			if (uv.y<=ye) MEM1
			else if (uv.y>yfe) MEM2
			else
				{
				v2=float(uv.y-ye)/sa; if (v2>1.0) { v2=1.0; } v1=1.0-v2;
				MEM12
				}
			}
		else if (type==2) // increasing box
			{
			cx=.5; cy=.5;
			dx=cx*float(i+rn)/float(n+rd);
			dy=cy*float(i+rn)/float(n+rd);
			if ((uv.x>=cx-dx)&&(uv.x<=cx+dx)&&(uv.y>=cy-dy)&&(uv.y<=cy+dy)) MEM2
			else MEM1
			}
		else if (type==3) // increasing diamond
			{
			cx=.5; cy=.5;
			rad=sqrt(cx*cx+cy*cy)*(float(i+rn)/float(n+rd));
			slope=-cy/cx;
			theta=atan(-slope);
			xc1=rad*cos(theta); yc1=rad*sin(theta);
			b=yc1-slope*xc1;
			cy=b; cx=(-b/slope);
			dy=abs(.5-uv.y);
			dx=abs(.5-uv.x);
			if (dx>cx) MEM1
			else if (dy>slope*dx+cy) MEM1
			else MEM2
			}
		else if (type==4) // increasing circle
			{
			cx=.5; cy=.5;
			rad=sqrt(cx/aspect*cx/aspect+cy*cy)*(float(i+rn)/float(n+rd));
			if (sqrt((uv.x-cx)/aspect*(uv.x-cx)/aspect+(uv.y-cy)*(uv.y-cy))>rad) MEM1
			else MEM2
			}
		else if (type==5) // increasing and decreasing diamonds
			{
			cx=.5; cy=.5;
			rad0=sqrt(cx*cx+cy*cy)/2.0;
			rad=sqrt(cx*cx+cy*cy)*(float(n-(i+1))/float(n+rd))/2.0;
			rad2=rad0+(rad0-rad);
			slope=-cy/cx;
			theta=atan(-slope);
			xc1=rad*cos(theta); yc1=rad*sin(theta);
			xc2=rad2*cos(theta); yc2=rad2*sin(theta);
			b=yc1-slope*xc1;
			b2=yc2-slope*xc2;
			cy=b; cx=(-b/slope);
			cy2=b2; cx2=(-b2/slope);
			dy=abs(.5-uv.y);
			dx=abs(.5-uv.x);
			if (dx>cx) MEM2
			else if (dy>slope*dx+cy) MEM2
			else MEM1
			if (dy>slope*dx+cy2) MEM1
			}
		else if (type==6) // four corner vanish
			{
			cx=.5; cy=.5;
			dx=cx*float(i+rn)/float(n+rd);
			dy=cy*float(i+rn)/float(n+rd);
			if ((uv.x>cx-dx)&&(uv.x<cx+dx)&&(uv.y>cy-dy)&&(uv.y<cy+dy)) MEM2
			else if ((uv.y<cy)&&(uv.x>cx-dx)&&(uv.x<cx+dx)) MEM2
			else if ((uv.y>cy)&&(uv.x>cx-dx)&&(uv.x<cx+dx)) MEM2
			else if ((uv.x<cx)&&(uv.y>cy-dy)&&(uv.y<cy+dy)) MEM2
			else if ((uv.x>cx)&&(uv.y>cy-dy)&&(uv.y<cy+dy)) MEM2
			else MEM1
			}
		else if (type==7) // increasing circle with soft edge
			{
			cx=.5; cy=.5;
			rad=sqrt(cx/aspect*cx/aspect+cy*cy)*(float(i+rn)/float(n+rd));
			//rad2=rad+10.0*sa;
			rad2=rad+sa;
			r1=sqrt((uv.x-cx)/aspect*(uv.x-cx)/aspect+(uv.y-cy)*(uv.y-cy));
			if (r1>rad2) MEM1
			else if (r1>rad) { v1=(r1-rad)/(rad2-rad); v2=1.0-v1; MEM12 }
			else MEM2
			}
		else if (type==8) // increasing diamond with soft edge
			{
			cx=.5; cy=.5;
			rad=sqrt(cx*cx+cy*cy)*(float(i+rn)/float(n+rd));
			//rad2=rad+7.0*sa;
			rad2=rad+.7*sa;
			slope=-cy/cx;
			theta=atan(-slope);
			xc1=rad*cos(theta); yc1=rad*sin(theta);
			xc2=rad2*cos(theta); yc2=rad2*sin(theta);
			b=yc1-slope*xc1;
			b2=yc2-slope*xc2;
			cy=b; cx=(-b/slope);
			cy2=b2; cx2=(-b2/slope);
			dy=abs(.5-uv.y);
			dx=abs(.5-uv.x);
			if (dx>cx2) MEM1
			else if (dy>slope*dx+cy2) MEM1
			else if (dy>slope*dx+cy)
				{
				vy1=slope*dx+b;
				vy2=slope*dx+b2;
				v1=(dy-vy1)/(vy2-vy1); v2=1.0-v1;
				MEM12
				}
			else MEM2
			}
		else if (type==9) // increasing & decreasing diamonds w/ soft edge
			{
			float xc1s,yc1s,xc2s,yc2s,rads,rad2s,bs,b2s,cxs,cys,cx2s,cy2s;

			cx=.5; cy=.5;
			rad0=sqrt(cx*cx+cy*cy)/2.0;
			rad=sqrt(cx*cx+cy*cy)*(float(n-(i+1))/float(n+rd))/2.0;
			//rads=rad+7.0*sa;
			rads=rad+.7*sa;
			rad2=rad0+(rad0-rad);
			//rad2s=rad2-7.0*sa;
			rad2s=rad2-.7*sa;
			slope=-cy/cx;
			theta=atan(-slope);
			xc1=rad*cos(theta); yc1=rad*sin(theta);
			xc2=rad2*cos(theta); yc2=rad2*sin(theta);
			xc1s=rads*cos(theta); yc1s=rads*sin(theta);
			xc2s=rad2s*cos(theta); yc2s=rad2s*sin(theta);
			b=yc1-slope*xc1;
			b2=yc2-slope*xc2;
			bs=yc1s-slope*xc1s;
			b2s=yc2s-slope*xc2s;
			cy=b; cx=(-b/slope);
			cy2=b2; cx2=(-b2/slope);
			cys=bs; cxs=(-bs/slope);
			cy2s=b2s; cx2s=(-b2s/slope);
			dy=abs(.5-uv.y);
			dx=abs(.5-uv.x);
			if (dx>cxs) MEM2
			else if (dy>slope*dx+cys) MEM2
			else if (dy>slope*dx+cy)
				{
				vy1=slope*dx+b;
				vy2=slope*dx+bs;
				v2=(dy-vy1)/(vy2-vy1); v1=1.0-v2;
				MEM12
				}
			else MEM1
			if (dy>slope*dx+cy2) MEM1
			else if (dy>slope*dx+cy2s)
				{
				vy1=slope*dx+b2;
				vy2=slope*dx+b2s;
				v2=(dy-vy1)/(vy2-vy1); v1=1.0-v2;
				MEM12
				}
			}
		else if (type==10) // increasing box with soft edge
			{
			cx=.5; cy=.5;
			dx=(cx*float(i+rn)/float(n+rd));
			dy=(cy*float(i+rn)/float(n+rd));
			dx2=(cx*float(i+rn)/float(n+rd)+sa);
			dy2=(cy*float(i+rn)/float(n+rd)+sa);
			if ((uv.x>=cx-dx)&&(uv.x<=cx+dx)&&(uv.y>=cy-dy)&&(uv.y<=cy+dy)) MEM2
