//#define SIMPLE


float N21(vec2 p) {
	vec3 a = fract(vec3(p.xyx) * vec3(213.897, 653.453, 253.098));
    a += dot(a, a.yzx + 79.76);
    return fract((a.x + a.y) * a.z);
}

vec2 GetPos(vec2 id, vec2 offs, float t) {
    float n = N21(id+offs);
    float n1 = fract(n*10.);
    float n2 = fract(n*100.);
    float a = t+n;
    return offs + vec2(sin(a*n1), cos(a*n2))*.4;
}

float GetT(vec2 ro, vec2 rd, vec2 p) {
	return dot(p-ro, rd); 
}

float LineDist(vec3 a, vec3 b, vec3 p) {
	return length(cross(b-a, p-a))/length(p-a);
}

float df_line( in vec2 a, in vec2 b, in vec2 p)
{
    vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0., 1.);	
	return length(pa - ba * h);
}

float line(vec2 a, vec2 b, vec2 uv) {
    float r1 = .04;
    float r2 = .01;

    float d = df_line(a, b, uv);
    float d2 = length(a-b);
    float fade = S(1.5, .5, d2);

    fade += S(.05, .02, abs(d2-.75));
    return S(r1, r2, d)*fade;
}

float NetLayer(vec2 st, float n, float t) {
    vec2 id = floor(st)+n;

    st = fract(st)-.5;

    vec2 p[9];
    int i=0;
    for(float y=-1.; y<=1.; y++) {
    	for(float x=-1.; x<=1.; x++) {
            p[i++] = GetPos(id, vec2(x,y), t);
    	}
    }

    float m = 0.;
    float sparkle = 0.;

    for(int i=0; i<9; i++) {
        m += line(p[4], p[i], st);

        float d = length(st-p[i]);

        float s = (.005/(d*d));
        s *= S(1., .7, d);
        float pulse = sin((fract(p[i].x)+fract(p[i].y)+t)*5.)*.4+.6;
        pulse = pow(pulse, 20.);

        s *= pulse;
        sparkle += s;
    }

    m += line(p[1], p[3], st);
	m += line(p[1], p[5], st);
    m += line(p[7], p[5], st);
    m += line(p[7], p[3], st);

    float sPhase = (sin(t+n)+sin(t*.1))*.25+.5;
    sPhase += pow(sin(t*.1)*.5+.5, 50.)*5.;
    m += sparkle*sPhase;//(*.5+.5);

    return m;
}

#define PI 3.14159265359
#define TWO_PI 6.28318530718

#define colorRange 24.0
vec4 fragColor;

float Band(float t, float start, float end, float blur) {

				float step1 = smoothstep(start - blur, start + blur, t);
				float step2 = smoothstep(end + blur, end - blur, t);

				return step1 * step2;//overlap edges

}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur) {
				float band1 = Band(uv.x, left, right, blur);
				float band2 = Band(uv.y, bottom, top, blur);

				return band1 * band2;//overlap horizontal and vertical bands

}
void getTriangle(vec2 uv, vec2 t0, vec2 t1, vec2 t2 ) {
				//Colors
				vec3 fgColor = vec3(0.741, 0.635, 0.471);
				vec3 bgColor = vec3(0.192, 0.329, 0.439);

				//Triangle barycentric coordinates defined on screen space
				/*vec2 t0 = vec2(0.5, 0.375);//base izq
				vec2 t1 = vec2(0.75, 0.25);//base der
				vec2 t2 = vec2(0.75, 0.50);//punta*/
				vec2 tCentroid = (t0 + t1 + t2) / 3.0;
				//Compute UV coordinates


				vec2 v0 = t2 - t0;
				vec2 v1 = t1 - t0;
				vec2 v2 = uv - t0;

				//Compute barycentric coordinates 
				float dot00 = dot(v0, v0);
				float dot01 = dot(v0, v1);
				float dot02 = dot(v0, v2);
				float dot11 = dot(v1, v1);
				float dot12 = dot(v1, v2);

				float invDenom = 1.0 / (dot00 * dot11 - dot01 * dot01);
				float baryX = (dot11 * dot02 - dot01 * dot12) * invDenom;
				float baryY = (dot00 * dot12 - dot01 * dot02) * invDenom;

				if ((baryX >= 0.0) && (baryY >= 0.0) && (baryX + baryY <= 1.0)) {
					fragColor = vec4(fgColor, 1.0);
				}
				//else discard;
				/*{
					fragColor = vec4(bgColor, 1.0);
				}*/

}

float triangle( in vec2 uv, in vec2 n ) {
    vec2 uuv = vec2(abs(uv.x), uv.y);
    return max(-uv.y, dot(n, uuv));
}
mat2 rot( in float a ) {
    float c = cos(a);
    float s = sin(a);
	return mat2(c,s,-s,c);	
}

float CircleShape(vec2 pos, float radius){    

    return step(radius, length(pos));
}

float polygon(vec2 pos, float rad, float sides){
    pos = pos * 2. - 1.;
    float angle = atan(pos.x, pos.y);
    float slice = PI * 2.0/ sides;

    return step(rad, cos(floor(0.5 + angle/ slice) * slice - angle) * length(pos));

}

