float rand (vec2 n)
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noisex(vec2 n)
{
	const vec2 d = vec2(0.0, 1.0);
  	vec2 b = floor(n), f = smoothstep(vec2(0.0), vec2(1.0), fract(n));
	return mix(mix(rand(b), rand(b + d.yx), f.x), mix(rand(b + d.xy), rand(b + d.yy), f.x), f.y);
}

float getPoly (vec2 uv, float gonSide, float gonAngle, float thickness, float percent)
{
    // N-Gon from : https://thndl.com/square-shaped-shaders.html
    float gonSlice = 6.28319 / gonSide;
    float gonSize = percent;
    float gonWidth = thickness;

    vec2 deltaUV = vec2(0.5) - uv;
    float deltaAngle = atan(deltaUV.x,deltaUV.y) + (gonAngle / PI);
    float dist = cos(floor(.5 + deltaAngle / gonSlice) * gonSlice - deltaAngle) * length(deltaUV.xy) * 2.0;
    return min(smoothstep(gonSize - gonWidth - 0.001, gonSize - gonWidth, dist), smoothstep(gonSize + gonWidth, gonSize + gonWidth - 0.001, dist));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord/iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;
    p.x *= iResolution.x/iResolution.y;
    //p*=rot(t);

    float r = length( p );
    float a = atan( p.y, p.x );

    float dd = 0.2*sin(4.0*iTime);
    float ss = 1.0 + clamp(1.0-r,0.0,1.0)*dd;

    r *= ss;

    vec3 col = vec3( 0.0, 0.3, 0.4 );

    float f = fbm( 5.0*p );
    col = mix( col, vec3(0.2,0.5,0.4), f );

    col = mix( col, vec3(0.9,0.6,0.2), 1.0-smoothstep(0.2,0.6,r) );

    a += 0.05*fbm( 20.0*p );

    f = smoothstep( 0.3, 1.0, fbm( vec2(20.0*a,6.0*r) ) );
    col = mix( col, vec3(1.0,1.0,1.0), f );

    f = smoothstep( 0.4, 0.9, fbm( vec2(15.0*a,10.0*r) ) );
    col *= 1.0-0.5*f;

    col *= 1.0-0.25*smoothstep( 0.6,0.8,r );

    f = 1.0-smoothstep( 0.0, 0.6, length2( mat2(0.6,0.8,-0.8,0.6)*(p-vec2(0.3,0.5) )*vec2(1.0,2.0)) );

    col += vec3(1.0,0.9,0.9)*f*0.985;

    col *= vec3(0.8+0.2*cos(r*a));

    f = 1.0-smoothstep( 0.2, 0.25, r );
    col = mix( col, vec3(0.0), f );

    f = smoothstep( 0.79, 0.82, r );
    col = mix( col, vec3(1.0), f );

    col *= 0.5 + 0.5*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.1);

    /////////////
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
	vec2 M = iMouse.xy/iResolution.xy-.5;

    float t = iTime*.1;

    float s = sin(t);
    float c = cos(t);
    mat2 rot = mat2(c, -s, s, c);
    vec2 st = uv*rot;  
	M *= rot*2.;

    float m = 0.;
    for(float i=0.; i<1.; i+=1./NUM_LAYERS) {
        float z = fract(t+i);
        float size = mix(15., 1., z);
        float fade = S(0., .6, z)*S(1., .8, z);

        m += fade * NetLayer(st*size-M*z, i, iTime);
    }

	float fft  = texelFetch( iChannel0, ivec2(.7,0), 0 ).x;
    float glow = -uv.y*fft*2.;

    vec3 baseCol = vec3(s, cos(t*.4), -sin(t*.24))*.4+.6;
    col *= mix(col, baseCol*m, 0.10);
    col += baseCol*glow;

    #ifdef SIMPLE
    uv *= 10.;
    col = vec3(1)*NetLayer(uv, 0., iTime);
    uv = fract(uv);
    //if(uv.x>.98 || uv.y>.98) col += 1.;
    #else
    col *= 1.-dot(uv,uv);
    t = mod(iTime, 230.);
    col *= S(0., 20., t)*S(224., 200., t);
    #endif

    //pulse star

    vec3 star;
    float l,z=t;
    for(int i=0;i<3;i++) {
        vec2 uv,p=fragCoord.xy/r;
        p-=.5;
        //p.x*=r.x/r.y;
        uv=p;
		z+=.07;
        l=length(p);
        uv+=p*abs(sin(l*7.-z*1.35));
        uv *= smoothstep(0.0005, 0.0006, l);
        //uv+= vec2(0.0,0.4);
        star[i]=0.05/length(uv);

        }

    //tube    
    float x=uv.x;
    float y=uv.y;
    //y=x;//vertical
    float d= smoothstep(.1,.2,length(y));
    x=y;//horiz
    float d2= fract(smoothstep(.1,.2,length(x+y)));//wide + or *
    d2+= mix(d2,fract(smoothstep(.1,.2,length(x+y))),1.0);//wide + or *

    //cones
    //move position
    p = vec2(.0, .0);

    x += .0;//move horizontal
    y += .0;//move vertical

    m = -(x - .5)*(x + .5);//distortion parabola
    y = uv.y - m;//distortion x
    //circle mask
    float mask = smoothstep(.03,.003,length(uv) - .4);//esfera Circle(uv, p, .5, .01);
    //mask += max(mask,  p.y-0.35);//cortar altura del circulo
    col += vec3(1.3, 0.75, 0.5)*mask;
    mask += smoothstep(.02,.01,length(uv) - .2);
    //cut another circle or add with symbol + or overlap multipling *
    //mask -= Circle(uv, p - vec2(.0, .0), .49, .01);
    mask += length(uv) + .1;//esferaCircle(uv, vec2(.0, .0), .3, .01);
    //col += vec3(0., 0., 1.)*mask;
    //cut another circle or add with symbol + or overlap multipling *
    mask -= CircleShape(uv,  .1);
    mask += length(uv) - .1;//esferaCircle(uv, vec2(.0, .0), .1, .01);
    //col += vec3(0.7, 0.8, 1.0)*mask;
    mask -= CircleShape(uv,  .1);
    p = uv - vec2(.0, .125);
    //getTriangle(p, vec2(0.5, 0.375), vec2(0.75, 0.25), vec2(0.75, 0.50));
    mask -= Rect(uv, .5, .9, -.08, .09, .001);
    //getTriangle(p, vec2(0.5, 0.375), vec2(0.25, 0.25), vec2(0.25, 0.5));
    mask -= Rect(uv, -.9, -.5, -.08, .09, .001);
    mask += smoothstep(.001,.001,triangle( abs(st*rot)-vec2(.0,0.22), vec2(.008,.006) ));


    //set color of circle and background
    if(d<.3) c = 0.0; else c= 1.0;

    //colorize circle
    //col /= vec3(1., 1., 1.)*mask;


    //polygon
    uv*=6.0;
    float polygon = polygon(rot* -uv+vec2(.5,.5), .6,3.0) * polygon(rot* uv+vec2(.5,.5), .6,3.0);

    col += polygon*star;

    //bpm
    uv*=.2;
    t = fract(iTime*(BPM/60.));
    float h = fract( 0.25 + 0.5*iChannelTime[0]*BPM/60.0 );
	f = 1.0-smoothstep( 0.0, 1.0, h );
	f *= smoothstep( 4.5, 4.51, iChannelTime[0] );
	r = length(uv-0.0) + 0.2*cos(25.0*h)*exp(-4.0*h);
	f = pow(f,0.5)*(1.0-smoothstep( 0.5, 0.55, r) );
	float rn = r/0.55;
	col = mix( col, vec3(0.4+1.5*rn,0.1+rn*rn,0.50)*rn, f );


	col = mix( col, vec3(1.0), smoothstep(  0.0,  3.0, iChannelTime[0] )*exp( -1.00*max(0.0,iChannelTime[0]- 2.5)) );
	col = mix( col, vec3(1.0), smoothstep( 16.0, 18.0, iChannelTime[0] )*exp( -0.75*max(0.0,iChannelTime[0]-19.0)) );

    ////drums
    float beattime = 1.0-fract(iTime/60.*BPM)/(BPM/60.0);
    float claptime = 1.0-fract((iTime-0.5)/120.*BPM)/(BPM/120.0);
    float hatintime = 1.0-fract((iTime-0.25)/60.*BPM)/(BPM/120.0);
    // Normalized pixel coordinates (from 0 to 1)
    uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv /= fract(uv);
    float kick = smoothstep(0.333,0.33,uv.x);
    float hat = smoothstep(0.66,0.662,uv.x);
    float clap = 1.0-max(kick,hat);

    col += vec3(kick*beattime,
                    hat*hatintime,
                    clap*claptime);
    #ifdef STROBEPREVENTION
    // desaturate to possibly reduce strobing?
    //col = ( col * 0.3 ) + 0.7;
    #endif

    ////bpmstuff
    vec2 pixelSz = vec2(1.0) / iResolution.xy;

	uv = fragCoord / iResolution.xy;
    vec4 final = texture(iChannel0, uv);

    const float stepsize = 0.1;
    const float stepmult = stepsize * 0.5;
    float samplescale = pixelSz.x * 64.0;

    for (float i=0.0; i<= 1.0; i+= stepsize)
    {
        float bias = i - 0.5;
        float samplerand = rand(uv) - 0.5;
        vec2 sampleuv = vec2(bias * samplescale + samplerand * samplescale, 0.0);

        final += texture(iChannel0, uv + sampleuv) * stepmult * (1.0 - abs(samplerand));
        final += texture(iChannel0, uv - sampleuv) * stepmult * (1.0 - abs(samplerand));
    }

    final.xyz = clamp(final.xyz, 0.0, 1.0);
    final.a = 1.0;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uvOrig = fragCoord / iResolution.xy;
    vec2 ctDelta = uvOrig - vec2(0.5);
    float ctLen = length(ctDelta);
    vec2 ctDir = normalize(ctDelta);

    // Aspect ratio corrected uv
    uv = uvOrig;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
	uv += 0.5;

    // Beat?
    //const float BPM = 120.0;
    const float BPS = 60.0 / BPM;
    const float BPS2 = BPS * 2.0;
    float beatDelta = mod(iTime, BPS) / BPS;
    float beatDelta2 = mod(iTime, BPS2) / BPS2;
    float beatIncr = 3.0 + mod(floor(iTime / BPS), 4.0);
    float beatIncr2 = 3.0 + mod(floor(iTime / BPS2), 4.0);

    float beatTime = iTime / BPS;
    float powerBeat = pow(1.0 - beatDelta, 1.4);

    // Flashy background
    float bgr = mod(beatIncr, 6.0) / 6.0;
    vec3 flash = vec3(sin(iTime * 2.0 + 60.0) * 0.5 + 0.5, cos(iTime * 4.0 + 0.5) * 0.5 + 0.5, abs(sin(iTime))) * 0.4 * pow(1.0 - beatDelta, 3.0);
    final.xyz = flash * (uv.y + sin(uv.x * 10.0) * 0.05);
    // Rad BG
    float radRand = rand(vec2(floor(uv.x * 8.0) / 8.0, beatIncr));
    vec2 raduv = vec2(uv.x, min(uv.y + radRand * 0.5, 1.0));

    float radIntensity = pow(min(powerBeat + radRand * 0.3, 1.0), 2.0) * 0.52 + abs(sin(beatTime * 10.0 + radRand * 42.0)) * 0.1;
    float radDelta = 1.0 - abs(radIntensity - raduv.y) * 2.0;
    float radPercent = max(round(sin(raduv.y * 128.0) * pow(radDelta, 2.0)) * pow(max(radDelta, 0.0), 6.0), smoothstep(radIntensity, radIntensity - 0.001, raduv.y));
    // radPercent *= pow(radDelta, 3.0);

    vec3 radColour = mix(vec3(0.0), vec3(0.9, 0.55, 0.15) * (1.0 + radRand * 0.1), radPercent);
    final.xyz += radColour * (1.0 + noise(uv * 5.0 + iTime) * 0.4 + noise(uv * 100.0 + iTime) * 0.9);

    // BG with stars?
    float ctAngle = atan(ctDir.y, ctDir.x) / PI_2 + 0.5;
    float starSamp = smoothstep(0.98, 1.0, noise(vec2(ctAngle * 2.0, ctLen * 0.05 - beatTime * 0.05) * 100.0));
    final.xyz += starSamp;
    final.xyz = clamp(final.xyz, 0.0, 1.0);
    // rotato potato
    // N-Gon from : https://thndl.com/square-shaped-shaders.html
    float gon1Side = beatIncr; // sides
    float gon1Angle = beatDelta * 4.0 - 2.0;
    float gon2Angle = beatDelta2 * 4.0 - 2.0;
    float gon1Width = 0.01 + pow(beatDelta, 3.0) * 0.1;

    float t1 = (beatDelta + abs(sin(beatDelta * 5.0)) * 0.2) / 1.2;
    float gon1Size = pow(1.0 - pow(1.0 - t1, 1.0), 3.0) * 2.0;
    float gon2Size = pow(1.0 - pow(1.0 - beatDelta2, 0.5), 2.5) * 2.0;

    float gon1 = getPoly(uv, gon1Side, gon1Angle, gon1Width, gon1Size);
    float gon2 = getPoly(uv, gon1Side + 1.0, gon2Angle, 0.01, gon2Size);

    final.xyz = mix(final.xyz, vec3(1.0), gon1);
    final.xyz += vec3(fract(0.5 - bgr), bgr, 0.5) * 0.25 * gon2; //mix(final.xyz, vec3(fract(0.5 - bgr), bgr, 0.5), gon2 * 0.2);
    for (float i=0.1; i<=1.0; i+=0.1)
    {
        float disp = 1.0 - pow(1.0 - i, 3.0);

        float gon3Size = pow(1.0 - pow(1.0 - t1 - 0.1 * disp, 1.0), 3.0) * 2.0;
        float gon3 = getPoly(uv, gon1Side, gon1Angle, gon1Width * 0.3 * i, gon3Size - 0.2);
    	final.xyz += vec3(sin(iTime * 2.0 + 60.0), tan(iTime * 4.0 + 0.5), abs(sin(iTime))) * gon3 * disp;
        // final.xyz = mix(final.xyz, vec3(sin(iTime * 2.0 + 60.0), tan(iTime * 4.0 + 0.5), abs(sin(iTime))), gon3);
    }

    // Output to screen
    final.xyz = clamp(final.xyz, 0.0, 1.0);
    final.a = 1.0;


    col += final.xyz;

    fragColor = vec4(col-polygon,final.a);
}

