mat2 rot(float a){float c=cos(a),s=sin(a);return mat2(c,-s,s,c);}
vec3 pal(float t){return .5+.5*cos(6.28*(1.*t+vec3(.0,.3,.7)));}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;
    vec3 p,d=normalize(vec3(uv,1.));
    vec3 c = vec3(0.);
    for(float i=0.,g,e;++i<99.;){

        p = g*d;
        p.x -=.5;;
        vec3 op=p;
        p.z=p.z+iTime*.1;

        vec3 id = floor(p);
        p.z = fract(p.z)-.5;

        vec3 pp = p;

        pp.x +=sin(id.z)*.2;

        pp.y +=cos(id.z)*.2;

        float gy = abs(dot(sin(pp*5.+pp.z+iTime*.1),cos(pp.yzx*5.)))/10.;

        float gy2 = abs(dot(sin(pp*5.+pp.z),cos(pp.zxy*5.)))/10.;
        vec2 q = vec2(length(pp.xy)-(1.5),pp.z);

        float h = length(q)-(.05);

        h = min(h,length(p.xy*rot(op.z)+vec2(gy,gy2))-(.01+sin(op.z+iTime)*.02));
        g+=e=min(.9,max(.0001,h));
        c+=pal(e+p.z)*.05/exp(i*i*e);
}
    fragColor = vec4(sqrt(c),1.0);
}
