            float lrat = nrat / lsum;
            lsum = lrat > (1.0 - CENTER_MIN) ? nrat * (1.0 / (1.0 - CENTER_MIN)) : lsum;
            l20 = l20 / lsum;
            l21 = l21 / lsum;
        }


        vec2 packed = vec2(pack(l20), pack(l21));

