        ivec2 offsets[8] = ivec2[](ivec2(-1, 1),ivec2( 0, 1),ivec2( 1, 1),ivec2( 1, 0),
                                   ivec2( 1,-1),ivec2( 0,-1),ivec2(-1,-1),ivec2(-1, 0));

