float unpackdist(vec4 a, vec4 b) {
    vec4 a0 = unpack(a.x);
    vec4 a1 = unpack(a.y);
    vec4 a2 = unpack(a.z);
    vec4 a3 = unpack(a.w);  

    vec4 b0 = unpack(b.x);
    vec4 b1 = unpack(b.y);
    vec4 b2 = unpack(b.z);
    vec4 b3 = unpack(b.w);

    float d0 = pdist(a0, b0);
    float d1 = pdist(a1, b1);
    float d2 = pdist(a2, b2);
    float d3 = pdist(a3, b3);

    return d0 + d1 + d2 + d3;
}

