float neighbor_dist(sampler2D channel0, sampler2D channel1, ivec2 iuv_base, ivec2 attacker_position, ivec2 iResolution) {
	vec4 v00 = D0(iuv_base, ivec2(0,0));
    vec4 v01 = D0(iuv_base, ivec2(1,0));
    vec4 v02 = D0(iuv_base, ivec2(0,1));
    vec4 v03 = D0(iuv_base, ivec2(1,1));

    vec4 w00 = D0(iuv_base, attacker_position + ivec2(0,0));
    vec4 w01 = D0(iuv_base, attacker_position + ivec2(1,0));
    vec4 w02 = D0(iuv_base, attacker_position + ivec2(0,1));
    vec4 w03 = D0(iuv_base, attacker_position + ivec2(1,1));

    vec4 v10 = D1(iuv_base, ivec2(0,0));
    vec4 v11 = D1(iuv_base, ivec2(1,0));
    vec4 v12 = D1(iuv_base, ivec2(0,1));
    vec4 v13 = D1(iuv_base, ivec2(1,1));

    vec4 w10 = D1(iuv_base, attacker_position + ivec2(0,0));
    vec4 w11 = D1(iuv_base, attacker_position + ivec2(1,0));
    vec4 w12 = D1(iuv_base, attacker_position + ivec2(0,1));
    vec4 w13 = D1(iuv_base, attacker_position + ivec2(1,1));

    vec4 d0 = vec4(unpackdist(v00, w00),
                   unpackdist(v01, w01),
                   unpackdist(v02, w02),
                   unpackdist(v03, w03));

    vec4 d1 = vec4(unpackdist(v10, w10),
                   unpackdist(v11, w11),
                   unpackdist(v12, w12),
                   unpackdist(v13, w13));

    return (dot(d0,vec4(1)) + dot(d1,vec4(1)));   
}

