// return color from pixel coordinate
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0, gl_FragCoord.xy/iResolution.xy);
}

// Dave Hoskins
// https://www.shadertoy.com/view/4djSRW
float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.zyx + 31.32);
    return fract((p3.x + p3.y) * p3.z);
}
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}


// Inigo Quilez
// https://iquilezles.org/articles/distfunctions
float smin( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }
float smoothing(float d1, float d2, float k) { return clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 ); }
float sdBox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// rotation matrix
mat2 rot(float a) { return mat2(cos(a),-sin(a),sin(a),cos(a)); }

#define repeat(p,r) (mod(p,r)-r/2.)

// Fork of "Taste of Noise 4" by leon. https://shadertoy.com/view/7ddXWf
// 2021-10-13 22:02:36

// taste of noise 5 by leon denise 2021/10/14
// result of experimentation with organic patterns
// using code from Inigo Quilez, David Hoskins and NuSan
// licensed under hippie love conspiracy

// global variable
float material;
float rng;

// sdf
float map (vec3 p)
{
    // time
    float t = iTime*0.1 + rng*0.1;

    // rotation parameter
    vec3 angle = vec3(.1,-.1,.1)+(t*2.+p*0.3);

    float size = sin(rng*3.14);

    // kif
    const int count = 8;
    float a = 1.0;
    float scene = 1000.;
    float shape = 1000.;
    for (int index = 0; index < count; ++index)
    {

        // fold
        p.x = abs(p.x)-0.7*a;

        // rotate
        p.xz *= rot(angle.y/a);
        p.yz *= rot(angle.x/a);
        p.yx *= rot(angle.z/a);

        // sphere
        shape = length(p)-0.4*a*size;

        // material blending
        material = mix(material, float(index), smoothing(shape, scene, 0.3*a));

        // add
        scene = smin(scene, shape, 0.1*a);

        // falloff
        a /= 1.3;
    }


    return scene;
}

// return color from pixel coordinate
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // reset color
    fragColor = vec4(0,0,0,1);
    material = 0.0;

    // camera coordinates
    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;
    vec3 eye = vec3(0,0,-4.);
    vec3 at = vec3(0,0,0);
    vec3 z = normalize(at-eye);
    vec3 x = normalize(cross(z, vec3(0,1,0)));
    vec3 y = normalize(cross(x, z));
    vec3 ray = normalize(vec3(z * 2. + uv.x * x + uv.y * y));
    vec3 pos = eye;

    // white noise
    vec3 seed = vec3(gl_FragCoord.xy, iTime);
    rng = hash13(seed);

    // raymarch
    const int steps = 20;
    for (int index = steps; index > 0; --index)
    {
        // volume estimation
        float dist = map(pos);
        if (dist < 0.01)
        {
            float shade = float(index)/float(steps);

            // compute normal by NuSan (https://www.shadertoy.com/view/3sBGzV)
            vec2 off=vec2(.001,0);
            vec3 normal = normalize(map(pos)-vec3(map(pos-off.xyy), map(pos-off.yxy), map(pos-off.yyx)));

            // Inigo Quilez color palette (https://iquilezles.org/articles/palettes)
            vec3 tint = vec3(.5)+vec3(.5)*cos(vec3(3,2,1)+material*.3+length(pos)*2.);

            // lighting
            float ld = dot(reflect(ray, normal), vec3(0,1,0))*0.5+0.5;
            vec3 light = vec3(1.000,1.000,1.000) * pow(ld, 2.) * 0.5;
            ld = dot(reflect(ray, normal), vec3(0,-1,0))*0.5+0.5;
            light += vec3(0.859,0.122,0.455) * pow(ld, 0.5)*.5;

            // pixel color
            fragColor.rgb = (tint + light) * shade;//pow(shade, 1.3);

            break;
        }

        // dithering
        dist *= 0.7 + .1 * rng;

        // raymarch
        pos += ray * dist;
    }

    // temporal buffer
    fragColor = max(fragColor, texture(iChannel0, gl_FragCoord.xy/iResolution.xy) - 0.01);
}
