// object transformation
vec3 rotate_x(vec3 p, float phi)
{   float c = cos(phi);	float s = sin(phi);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}
vec3 rotate_y(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x + s*p.z, p.y, c*p.z - s*p.x);
}
vec3 rotate_z(vec3 p, float phi)
{	float c = cos(phi);	float s = sin(phi);
	return vec3(c*p.x - s*p.y, s*p.x + c*p.y, p.z);
}

vec2 rotatev2(vec2 p, float ang)
{   float c = cos(ang);
    float s = sin(ang);
    return vec2(p.x*c - p.y*s, p.x*s + p.y*c);
}


vec3 moverObj(vec3 p, vec3 p1, vec3 p2,float tInc, float tA ,float tB)
{   vec3 pres=p1;
    if (tInc<tA) pres=p;

    if (tInc>=tA && tInc<tB)
    { pres.x=mix(p1.x, p2.x, smoothstep(tA, tB,tInc));
      pres.y=mix(p1.y, p2.y, smoothstep(tA, tB,tInc));
      pres.z=mix(p1.z, p2.z, smoothstep(tA, tB,tInc));
      pres=p-pres;
    }

    if (tInc>tB)  pres=p-p2;

    return pres;
}


vec3 Nave(vec3 p)
{	vec3 res= vec3(9999.0, -1.0,-1.0);
    float sdcy1= sdCylinderXZ( p, vec2(5.0,0.25) );
    float sdcy2= sdCylinderXZ( p-vec3(0.0,0.25,.0), vec2(5.0,0.15) );
    float sds1= sdSphere(p-vec3(0.0,1.0,0.0), 1.0 );
    res =opU3(res, vec3(sdcy1,3.0,-1.0));
    res =opU3(res, vec3(sdcy2,1.0,-1.0));
    res =opU3(res, vec3(sds1,1.0,-1.0));

    return res;
}


vec3 Arbol(vec3 p)
{	vec3 res= vec3(9999.0, -1.0,-1.0);
    p.z-=5.0;
    p.y+=2.0;
    float sdTronco=sdCylinderXZ(p-vec3(0.0,4.0,0.0), vec2(0.7,8.0) );
    float sdcy1=sdCylinderXZ(p-vec3(0.0,4.0,0.0), vec2(5.0,0.5) );
    float sdcy2=sdCylinderXZ(p-vec3(0.0,6.0,0.0), vec2(4.0,0.5) );
    float sdcy3=sdCylinderXZ(p-vec3(0.0,8.0,0.0), vec2(3.0,0.5) );
    float sdcy4=sdCylinderXZ(p-vec3(0.0,10.0,0.0), vec2(2.0,0.5) );
    float sdcy5=sdCylinderXZ(p-vec3(0.0,12.0,0.0), vec2(1.0,0.5) );

    res =opU3(res, vec3(sdTronco,1.0,-1.0));
    res =opU3(res, vec3(sdcy1,3.0,-1.0));
    res =opU3(res, vec3(sdcy2,3.0,-1.0));
    res =opU3(res, vec3(sdcy3,3.0,-1.0));
    res =opU3(res, vec3(sdcy4,3.0,-1.0));
    res =opU3(res, vec3(sdcy5,3.0,-1.0));


    return res;
}

