vec3 GetDist(vec3 p  ) 
{	vec3 res= vec3(9999.0, -1.0,-1.0);  vec3 pp=p;
	float planeDist1 = p.y+0.0;  //piso inf

    res =opU3(res, vec3(planeDist1,100.0,-1.0)); //inf
    res =opU3(res, vec3(planeDist1,-1.0,7.0)); //inf

    p.y=p.y-5.0;

    vec3 p1=vec3(0.0,.0,.0);
    vec3 p2=vec3(15.0,0.0,.0);
    vec3 p3=vec3(15.0,10.0,.0);
    vec3 p4=vec3(15.0,10.0,30.0);
    vec3 p5=vec3(30.0,10.0,60.0);


    float vel=5.0;
    float timeMov=mod(iTime*vel,30.0);

    vec3 pnew1=moverObj( p, p1, p2,timeMov,1.0, 5.0);
    vec3 pnew2=moverObj( p, p2, p3,timeMov,5.0, 10.0);
    vec3 pnew3=moverObj( p, p3, p4,timeMov,10.0, 20.0);
    vec3 pnew4=moverObj( p, p4, p5,timeMov,20.0, 30.0);

    vec3 sdres;
     if (timeMov<5.0)
        sdres= Nave(pnew1);
    else if(timeMov<10.0)
        sdres= Nave(pnew2);
    else if(timeMov<20.0)
        sdres= Nave(pnew3);
    else if(timeMov<30.0)
        sdres= Nave(pnew4);


    res =opU3(res, sdres);

    vec3 ar1= Arbol(p-vec3(.0,.0,.0));
    vec3 ar2= Arbol(p-vec3(30.0,.0,.0));
    vec3 ar3= Arbol(p-vec3(40.0,.0,40.0));
    vec3 ar4= Arbol(p-vec3(60.0,.0,80.0));

    res =opU3(res, ar1);
    res =opU3(res, ar2);
    res =opU3(res, ar3);
    res =opU3(res, ar4);

    return res;
}

